import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { AutresComplicationsSD } from 'src/app/models/AutresComplicationsSD';
import { AutrecompService } from 'src/app/services/autrecomp.service';

@Component({
  selector: 'app-autrecomp',
  templateUrl: './autrecomp.component.html',
  styleUrls: ['./autrecomp.component.css']
})
export class AutrecompComponent {
  AutresComplicationsSD: AutresComplicationsSD = new AutresComplicationsSD();
  errorMessage: string = '';

  constructor(private AutrecompService: AutrecompService) {}
 

  onSubmit() {  
    this.AutrecompService.autrecomp(this.AutresComplicationsSD).subscribe(
      response => {
        console.log('Complication added successfully', response);
        this.errorMessage = '';
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Complication', error);
        this.errorMessage = error.error.title;
        if (error.error.errors) {
          for (const [field, errors] of Object.entries(error.error.errors)) {
            console.error(`Validation error for ${field}:`, errors);
          }
        }
      }
    );
  }
  
}

<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
  
  .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Style for CNAM radio buttons */
    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }

    .radio-group input[type="radio"] {
      margin-right: 5px;
    }

    .radio-group label {
      margin: 0;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" >
     
      <h1>Anomalie Glucidique</h1>
    
      <!-- Patient Id-->
      <div>
        <label for="PatientId">PatientId</label>
        <input type="number" id="patientId" [(ngModel)]="AutresComplicationsSD.PatientId" name="PatientId" >
      </div>
     

      <!-- UlcereJambe -->
  <div>
    <label>Ulcere de Jambe:</label>
    <div class="radio-group">
      <!-- Oui radio button first -->
      <input type="radio" id="aOui" [(ngModel)]="AutresComplicationsSD.UlcereJambe" [value]="true" name="UlcereJambe" required>
      <label for="aOui">Oui</label>
      
      <!-- No radio button second -->
      <input type="radio" id="aNon" [(ngModel)]="AutresComplicationsSD.UlcereJambe" [value]="false" name="UlcereJambe" required>
      <label for="aNon">Non</label>
    </div>
  </div>

      
      

        <!-- Date -->
        <div *ngIf=" AutresComplicationsSD.UlcereJambe">
            <label for="DateUlcereJambe">Date</label>
          <input type="date" id="DateUlcereJambe" [(ngModel)]="AutresComplicationsSD.DateUlcereJambe" name="DateUlcereJambe" >
        </div>
 <!-- Siege -->
 <div *ngIf=" AutresComplicationsSD.UlcereJambe">
    <label for="SiegeUlcereJambe">Siege</label>
  <input type="number" id="SiegeUlcereJambe" [(ngModel)]="AutresComplicationsSD.SiegeUlcereJambe" name="SiegeUlcereJambe" >
</div>
    
 <!-- Evolution -->
 <div>
    <label for="Evolution">Evolution:</label>
    <select id="Evolution" [(ngModel)]="ComplicationGlucidique.EvalutionAnomalieGlucidique" name="Evolution" >
      <option value="Guérison">Guérison</option>
      <option value="Amélioration sans guérison">Amélioration sans guérison</option>
      <option value="Persistance ou aggravation">Persistance ou aggravation </option>

    </select>
  </div>
   <!-- NecessiteMedicamentAntiDiabetique -->
 <div>
    <label for="NecessiteMedicamentAntiDiabetique">Pancréas:</label>
    <select id="NecessiteMedicamentAntiDiabetique" [(ngModel)]="ComplicationGlucidique.NecessiteMedicamentAntiDiabetique" name="NecessiteMedicamentAntiDiabetique" >
      <option value="Aucune">Aucune</option>
      <option value="Anti-diabétique oral">Anti-diabétique oral</option>
      <option value="Insuline">Insuline</option>

    </select>
  </div>
 <!-- AnomalieGlucidiqueActuelle -->
 <div>
    <label for="AnomaliesGlucidiquesActuel">Pancréas:</label>
