import { Component } from '@angular/core';

@Component({
  selector: 'app-status',
  templateUrl: './status.component.html',
  styleUrls: ['./status.component.css']
})
export class StatusComponent {

}
<app-content title="Gestion des utilisateurs">
     </app-content>
     <!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>User Form</title>
  <style>
    /* Form Container */
    .form-container {
      max-width: 400px;
      margin: 0 auto;
    }

    /* Form Style */
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    /* Form Input Fields */
    .form-container input[type="text"],
    .form-container input[type="email"],
    .form-container input[type="password"] {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    /* Form Submit Button */
    .form-container input[type="submit"] {
      background-color: #000 !important; /* Set background color to black */
      color: #fff !important; /* Set text color to white */
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      width: 100%;
      transition: background-color 0.3s ease; /* Smooth transition for hover effect */
    }

    /* Form Submit Button Hover Effect */
    .form-container input[type="submit"]:hover {
      background-color: #000; /* Change background color to black on hover */
      color: #fff; /* Change text color to white on hover */
    }

    /* Custom Select Style */
    .form-container select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form action="/submit-user" method="POST">
 

      <label for="userName">Username:</label>
      <input type="text" id="userName" name="userName" pattern="[A-Za-z ]{3,20}" title="Username must be letters only, with spaces allowed, and between 3 to 20 characters long." required>

      <label for="password">Password:</label>
      <input type="password" id="password" name="password" required>

      <label for="active">Active:</label>
      <input type="checkbox" id="active" name="active" checked>

      <label for="recycleBin">Recycle Bin:</label>
      <input type="checkbox" id="recycleBin" name="recycleBin">

      <label for="activeUntil">Active Until:</label>
      <input type="date" id="activeUntil" name="activeUntil">

      <label for="dateCreation">Date Creation:</label>
      <input type="date" id="dateCreation" name="dateCreation" required>

      <label for="roleId">Role ID:</label>
      <input type="number" id="roleId" name="roleId" required>

      <label for="nom">Nom:</label>
      <input type="text" id="nom" name="nom" required>

      <label for="prenom">Prénom:</label>
      <input type="text" id="prenom" name="prenom" required>

      <label for="mobile">Mobile:</label>
      <input type="text" id="mobile" name="mobile">

      <label for="email">Email:</label>
      <input type="email" id="email" name="email">

      <label for="role">Role:</label>
      <select id="role" name="role" required>
        <option value="">Select Role</option>
        <option value="1">Admin</option>
        <option value="2">Medecin</option>
        <option value="3">Infirmier</option>
        <option value="4">Pharmacien</option>
      </select>

      <input type="submit" value="Submit">
    </form>
  </div>
</body>
</html>
