import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { ComplicationAgAnemie } from 'src/app/models/ComplicationAgAnemie';
import { AgService } from 'src/app/services/ag.service';

@Component({
  selector: 'app-ag',
  templateUrl: './ag.component.html',
  styleUrls: ['./ag.component.css']
})
export class AgComponent {
  complications: ComplicationAgAnemie[] = [new ComplicationAgAnemie()];
  errorMessage: string = '';

  constructor(private AgService: AgService) {}

  addComplication() {
    this.complications.push(new ComplicationAgAnemie());
  }

  onSubmit() {
    for (let complication of this.complications) {
      this.AgService.addag(complication).subscribe(
        response => {
          console.log('Complication added successfully', response);
          this.errorMessage = '';
        },
        (error: HttpErrorResponse) => {
          console.error('Error adding Complication', error);
          this.errorMessage = error.error.title;
          if (error.error.errors) {
            for (const [field, errors] of Object.entries(error.error.errors)) {
              console.error(`Validation error for ${field}:`, errors);
            }
          }
        }
      );
    }
  }
}
