import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { IrmCoeur } from 'src/app/models/IrmCoeur';
import { IrmcoeurService } from 'src/app/services/irmcoeur.service';

@Component({
  selector: 'app-updateirmc',
  templateUrl: './updateirmc.component.html',
  styleUrls: ['./updateirmc.component.css']
})
export class UpdateirmcComponent {
  IrmCoeur: any= {};
  errorMessage: string = '';

  constructor(
    private IrmcoeurService: IrmcoeurService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  
  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const PatientID = params.get('PatientID');
      if (PatientID) {
        const id = parseInt(PatientID, 10);
        if (!isNaN(id)) {
          this.getIrmCoeurById(id);
        } else {
          console.error('Invalid patient ID:', PatientID);
        }
      } else {
        console.error('Patient ID is missing in route');
      }
    });
  }
  
  
  private getIrmCoeurById(id: number) {
    this.IrmcoeurService.getIrmCoeurById(id).subscribe(
      (data: IrmCoeur) => {
        this.IrmCoeur = data;
        // Optionally log the data to ensure it's correctly set
        console.log('Formatted data:', this.IrmCoeur);
        this.IrmCoeur.dateIrm = this.formatDate(this.IrmCoeur.dateIrm);
      console.log("testt date ");
       console.log(this.IrmCoeur.dateIrm);
      },
      (error: HttpErrorResponse) => {
        this.errorMessage = error.message;
        console.error('Error fetching complication data:', error);
      }
    );
  }
  formatDate(dateString: string): string {
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are 0-based
    const year = date.getFullYear();
    return `${year}-${month}-${day}`;
  }
  
  

  onSubmit() {
    const patientID = parseInt(this.route.snapshot.paramMap.get('PatientID')!, 10);
    if (!isNaN(patientID)) {
        this.IrmcoeurService.update(patientID, this.IrmCoeur).subscribe(
            response => {
                console.log('Update successful', response);
                //this.router.navigate(['/']); // Adjust this to your desired route after successful update
            },
            (error: HttpErrorResponse) => {
                this.errorMessage = error.message;
                console.error('Error updating complication data:', error);
            }
        );
    } else {
        console.error('Invalid patient ID:', patientID);
    }
}

}
