import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { ComplicationDeveloppementPubertaire } from 'src/app/models/ComplicationDeveloppementPubertaire';
import { DevpubService } from 'src/app/services/devpub.service';

@Component({
  selector: 'app-updatedevpub',
  templateUrl: './updatedevpub.component.html',
  styleUrls: ['./updatedevpub.component.css']
})
export class UpdatedevpubComponent {
  ComplicationDeveloppementPubertaire: any= {};
  errorMessage: string = '';

  constructor(
    private DevpubService: DevpubService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  
  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const PatientID = params.get('PatientID');
      if (PatientID) {
        const id = parseInt(PatientID, 10);
        if (!isNaN(id)) {
          this.getComplicationdevpubById(id);
        } else {
          console.error('Invalid patient ID:', PatientID);
        }
      } else {
        console.error('Patient ID is missing in route');
      }
    });
  }
  
  
  private getComplicationdevpubById(id: number) {
    this.DevpubService.getComplicationdevpubById(id).subscribe(
      (data: ComplicationDeveloppementPubertaire) => {
        this.ComplicationDeveloppementPubertaire = data;
        // Optionally log the data to ensure it's correctly set
        console.log('Formatted data:', this.ComplicationDeveloppementPubertaire);
      },
      (error: HttpErrorResponse) => {
        this.errorMessage = error.message;
        console.error('Error fetching complication data:', error);
      }
    );
  }

  
  

  onSubmit() {
    const patientID = parseInt(this.route.snapshot.paramMap.get('PatientID')!, 10);
    if (!isNaN(patientID)) {
        this.DevpubService.update(patientID, this.ComplicationDeveloppementPubertaire).subscribe(
            response => {
                console.log('Update successful', response);
                //this.router.navigate(['/']); // Adjust this to your desired route after successful update
            },
            (error: HttpErrorResponse) => {
                this.errorMessage = error.message;
                console.error('Error updating complication data:', error);
            }
        );
    } else {
        console.error('Invalid patient ID:', patientID);
    }
}

}
