import { Component } from '@angular/core';

@Component({
  selector: 'app-edit-profile',
  templateUrl: './edit-profile.component.html',
  styleUrls: ['./edit-profile.component.css']
})
export class EditProfileComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>User Form</title>
  <style>
    /* Form Container */
    .form-container {
      max-width: 600px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    /* Form Style */
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    /* Form Input Fields */
    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize; /* Convert label text to title case */
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center; /* Center content horizontally */
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    /* Form Submit Button Hover Effect */
    .form-container form input[type="submit"]:hover {
      background-color: #333;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" action="/submit-user">

      <div>
        <label for="userName">Nom d'utilisateur:</label>
        <input type="text" id="userName" [(ngModel)]="user.userName" name="userName" pattern="[A-Za-z ]{3,20}" title="Username must be letters only, with spaces allowed, and between 3 to 20 characters long." required>
      </div>

      <div>
        <label for="password">Votre ancien mot de passe :</label>
        <input type="password" id="password" [(ngModel)]="user.password" name="password" required>
      </div>
      <div>
        <label for="password">Votre nouveau mot de passe :</label>
        <input type="password" id="password" [(ngModel)]="user.password" name="password" required>
      </div>

      
      <div>
        <label for="mobile">Téléphone:</label>
        <input type="text" id="mobile" [(ngModel)]="user.mobile" name="mobile">
      </div>

      

      <div>
        <label for="nom">Nom:</label>
        <input type="text" id="nom" [(ngModel)]="user.nom" name="nom" required>
      </div>

      <div>
        <label for="prenom">Prénom:</label>
        <input type="text" id="prenom" [(ngModel)]="user.prenom" name="prenom" required>
      </div>

      <div>
        <label for="email">Email:</label>
        <input type="email" id="email" [(ngModel)]="user.email" name="email">
      </div>
    

     

      

    

      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
   
    </form>
  </div>
</body>
</html>
