import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { IrmFoie } from 'src/app/models/IrmFoie';
import { IrmfoieService } from 'src/app/services/irmfoie.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-irmfoie',
  templateUrl: './irmfoie.component.html',
  styleUrls: ['./irmfoie.component.css']
})
export class IrmfoieComponent {
  IrmFoie: IrmFoie = new IrmFoie();
  errorMessage: string = '';
  patientId: string | null = null;

  constructor(private irmfoieService: IrmfoieService, private router:Router) {}
  private formatDateToYYYYMMDD(date: Date): string {
    return date.toISOString().split('T')[0];
  }
  ngOnInit(): void {
    // Retrieve the PatientId from local storage
    this.patientId = localStorage.getItem('patientId');
  
    if (this.patientId && this.patientId !== '0') {
      console.log('Patient ID retrieved from local storage:', this.patientId);
    } else {
      console.error('No valid Patient ID found in local storage.');
    }
  }

  nSubmit() {
    const now = new Date();
    this.IrmFoie.DateIrm = this.formatDateToYYYYMMDD(now);  // Format `yyyy-MM-dd`
  
    if (this.IrmFoie.DateIrm) {
      this.IrmFoie.DateIrm = this.formatDateToYYYYMMDD(new Date(this.IrmFoie.DateIrm));
    } else {
      this.IrmFoie.DateIrm = this.formatDateToYYYYMMDD(now); 
    }
  
    // Convertir les dates en format ISO 8601 avant de les envoyer au backend
    const complicationToSend = {
      ...this.IrmFoie,
      DateEchocardiographie: new Date(this.IrmFoie.DateIrm).toISOString(),
    };
  
    this.irmfoieService.addIrmfoie(complicationToSend).subscribe(
      response => {
        console.log('IrmFoie added successfully:', response);
        this.errorMessage = '';

        // After successful submission, navigate to the next page
        if (this.patientId) {
          this.router.navigate(['admin/glucidique', this.patientId]).then(() => {
            console.log('Navigated to the next page with patientId:', this.patientId);
          }).catch(err => {
            console.error('Navigation error:', err);
          });
        } else {
          console.error('No patientId found. Navigation aborted.');
        }
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Irm:', error);
        this.errorMessage = error.error.title;
        if (error.error.errors) {
          for (const [field, errors] of Object.entries(error.error.errors)) {
            console.error(`Validation error for ${field}:`, errors);
          }
        }
      }
    );
  }
}
