import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Chelateur } from 'src/app/models/Chelateur';
import { TraitementChelateurReçu } from 'src/app/models/TraitementChelateurReçu';
import { ChelateurServiceService } from 'src/app/services/chelateur-service.service';
import { TreatChelateurService } from 'src/app/services/treat-chelateur.service';

@Component({
  selector: 'app-update-treat-chelat',
  templateUrl: './update-treat-chelat.component.html',
  styleUrls: ['./update-treat-chelat.component.css']
})
export class UpdateTreatChelatComponent {
  chelateurs: any= {};
  errorMessage: string = '';
  traitements: TraitementChelateurReçu[] = [];
  traitementChelateurReçuMap: { [key: number]: TraitementChelateurReçu } = {};
  
  constructor(
    private TreatChelateurService: TreatChelateurService,
    private router: Router,
    private chelateurService: ChelateurServiceService,
    private route: ActivatedRoute
  ) {}

  
  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const IdVisite = params.get('IdVisite');
      if (IdVisite) {
        const id = parseInt(IdVisite, 10);
        if (!isNaN(id)) {
          this.getById(id);
        } else {
          console.error('Invalid visit ID:', IdVisite);
        }
      } else {
        console.error('visit ID is missing in route');
      }
    });
  }
  
  
  private getById(id: number) {
    this.TreatChelateurService.getById(id).subscribe(
      (data: TraitementChelateurReçu) => {
        this.chelateurs = data;
        // Optionally log the data to ensure it's correctly set
        console.log('Formatted data:', this.chelateurs);
      },
      (error: HttpErrorResponse) => {
        this.errorMessage = error.message;
        console.error('Error fetching complication data:', error);
      }
    );
  }

   loadChelateurs() {
    this.chelateurService.getChelateurs().subscribe(
      (data: Chelateur[]) => {
        this.chelateurs = data;
      },
      error => {
        console.error('Error fetching chelateurs', error);
      }
    );
  }

 
  isChecked(ChelateurId: number): boolean {
    return this.traitements.some(traitement => traitement.ChelateurId === ChelateurId);
  }

  onChelateurChange(event: any) {
    const chelateurId = +event.target.value;
    const isChecked = event.target.checked;
  
    if (isChecked) {
      const newTraitement = this.getTraitementByChelateurId(chelateurId);
      this.traitements.push(newTraitement);
    } else {
      this.traitements = this.traitements.filter(traitement => traitement.ChelateurId !== chelateurId);
      delete this.traitementChelateurReçuMap[chelateurId];
    }
  }
  
  getTraitementByChelateurId(chelateurId: number): TraitementChelateurReçu {
    if (!this.traitementChelateurReçuMap[chelateurId]) {
      this.traitementChelateurReçuMap[chelateurId] = {
        Observance: '',
        Posologie: 0,
        Depuis: 0,
        Voie: '',
        VisiteId: 0,
        ChelateurId: chelateurId
      };
    }
    return this.traitementChelateurReçuMap[chelateurId];
  }
  

  onSubmit() {
    const IdVisite = parseInt(this.route.snapshot.paramMap.get('IdVisite')!, 10);
    if (!isNaN(IdVisite)) {
        this.TreatChelateurService.update(IdVisite, this.chelateurs).subscribe(
            response => {
                console.log('Update successful', response);
                //this.router.navigate(['/']); // Adjust this to your desired route after successful update
            },
            (error: HttpErrorResponse) => {
                this.errorMessage = error.message;
                console.error('Error updating complication data:', error);
            }
        );
    } else {
        console.error('Invalid IdVisite :', IdVisite);
    }
}

}
