import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { ComplicationGlucidique } from 'src/app/models/ComplicationGlucidique';
import { GlucidiqueService } from 'src/app/services/glucidique.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-glucidique',
  templateUrl: './glucidique.component.html',
  styleUrls: ['./glucidique.component.css']
})
export class GlucidiqueComponent {
  ComplicationGlucidique: ComplicationGlucidique = new ComplicationGlucidique();
  errorMessage: string = '';
  patientId: string | null = null;

  constructor(private GlucidiqueService: GlucidiqueService,private router:Router) {}
  ngOnInit(): void {
    // Retrieve the PatientId from local storage
    this.patientId = localStorage.getItem('patientId');
  
    if (this.patientId && this.patientId !== '0') {
      console.log('Patient ID retrieved from local storage:', this.patientId);
    } else {
      console.error('No valid Patient ID found in local storage.');
    }
  }

  onSubmit() {  
    this.GlucidiqueService.addGlucidique(this.ComplicationGlucidique).subscribe(
      response => {
        console.log('Complication added successfully', response);
        this.errorMessage = '';

        // Navigate to the next page with the patientId
        if (this.patientId) {
          this.router.navigate(['admin/devpub', this.patientId]).then(() => {
            console.log('Navigated to the next page with patientId:', this.patientId);
          }).catch(err => {
            console.error('Navigation error:', err);
          });
        } else {
          console.error('No patientId found. Navigation aborted.');
        }
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Complication', error);
        this.errorMessage = error.error.title;
        if (error.error.errors) {
          for (const [field, errors] of Object.entries(error.error.errors)) {
            console.error(`Validation error for ${field}:`, errors);
          }
        }
      }
    );
  }
  
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
  
  .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Style for CNAM radio buttons */
    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }

    .radio-group input[type="radio"] {
      margin-right: 5px;
    }

    .radio-group label {
      margin: 0;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" >
     
      <h1>Anomalie Glucidique</h1>
     
      <!-- AnomalieGlucidiqueAuCoursSuivi -->
      <div>
        <label for="AnomalieGlucidiqueAuCoursSuivi">Pancréas:</label>
        <select id="AnomalieGlucidiqueAuCoursSuivi" [(ngModel)]="ComplicationGlucidique.AnomalieGlucidiqueAuCoursSuivi" name="AnomalieGlucidiqueAuCoursSuivi" required>
            <option value="Aucune">Aucune</option>
            <option value="Hyperglycemie modérée à jeun">Hyperglycemie modérée à jeun</option>
            <option value="Introlerance au glucose">Introlerance au glucose </option>
            <option value="Diabète">Diabète</option>

        </select>
      </div>

      
      

        <!-- AgeApparitionAnomaliesGlucidique -->
        <div>
          <label for="AgeApparitionAnomaliesGlucidique">Age d'apparition</label>
          <input type="number" id="AgeApparitionAnomaliesGlucidique" [(ngModel)]="ComplicationGlucidique.AgeApparitionAnomaliesGlucidique" name="AgeApparitionAnomaliesGlucidique" required>
        </div>

    
 <!-- Evolution -->
 <div>
    <label for="Evolution">Evolution:</label>
    <select id="Evolution" [(ngModel)]="ComplicationGlucidique.EvalutionAnomalieGlucidique" name="Evolution" >
      <option value="Guérison">Guérison</option>
      <option value="Amélioration sans guérison">Amélioration sans guérison</option>
      <option value="Persistance ou aggravation">Persistance ou aggravation </option>

    </select>
  </div>
   <!-- NecessiteMedicamentAntiDiabetique -->
 <div>
    <label for="NecessiteMedicamentAntiDiabetique">Nécessité d'un Médicament anti-diabétique:</label>
    <select id="NecessiteMedicamentAntiDiabetique" [(ngModel)]="ComplicationGlucidique.NecessiteMedicamentAntiDiabetique" name="NecessiteMedicamentAntiDiabetique" >
      <option value="Aucune">Aucune</option>
      <option value="Anti-diabétique oral">Anti-diabétique oral</option>
      <option value="Insuline">Insuline</option>

    </select>
  </div>
 <!-- AnomalieGlucidiqueActuelle -->
 <div>
    <label for="AnomaliesGlucidiquesActuel">Anomalies glucidiques présents actuellement:</label>
    <select id="AnomaliesGlucidiquesActuel" [(ngModel)]="ComplicationGlucidique.AnomaliesGlucidiquesActuel" name="AnomaliesGlucidiquesActuel" >
        <option value="Aucune">Aucune</option>
        <option value="Hyperglycemie modérée à jeun">Hyperglycemie modérée à jeun</option>
        <option value="Introlerance au glucose">Introlerance au glucose </option>
        <option value="Diabète">Diabète</option>

    </select>
  </div>
 <!-- MedicamentAntidiabetiqueActuel -->
 <div>
    <label for="MedicamentAntidiabetiqueActuel">Médicament anti-diabétique actuel:</label>
    <select id="MedicamentAntidiabetiqueActuel" [(ngModel)]="ComplicationGlucidique.MedicamentAntidiabetiqueActuel" name="MedicamentAntidiabetiqueActuel" >
      <option value="Aucune">Aucune</option>
      <option value="Anti-diabétique oral">Anti-diabétique oral</option>
      <option value="Insuline">Insuline</option>

    </select>
  </div>
 

      
      <!-- Submit -->
      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
