import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { OrdonnancedeSortie } from 'src/app/models/OrdonnancedeSortie';
import { OrdonnancedeSortieService } from 'src/app/services/ordonnancede-sortie.service';

@Component({
  selector: 'app-ordonnancede-sortie',
  templateUrl: './ordonnancede-sortie.component.html',
  styleUrls: ['./ordonnancede-sortie.component.css']
})
export class OrdonnancedeSortieComponent {
  OrdonnancedeSorties: OrdonnancedeSortie[] = [new OrdonnancedeSortie()];
  errorMessage: string = '';

  constructor(private OrdonnancedeSortieService: OrdonnancedeSortieService) {}

  

  addCriseForm() {
    this.OrdonnancedeSorties.push(new OrdonnancedeSortie());
  }

  onSubmit() {
    this.errorMessage = ''; // Clear previous errors
    this.OrdonnancedeSorties.forEach(complication => {
      this.OrdonnancedeSortieService.addord(complication).subscribe(
        response => {
          console.log('Complication added successfully', response);
        },
        (error: HttpErrorResponse) => {
          console.error('Error adding complication', error);
          this.errorMessage = error.error.title || 'An error occurred while adding the complication.';
        }
      );
    });
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }
    .form-container h1, .form-container h2 {
      text-align: center;
      font-weight: bold;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      padding-bottom: 10px;
      display: inline-block;
      margin-bottom: 30px;
    }
    .form-container h1 {
      font-size: 36px;
      color: #2c3e50;
      border-bottom: 3px solid #3498db;
    }
    .form-container h2 {
      font-size: 30px;
      color: #c95355;
      border-bottom: 3px solid #f12349;
    }
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }
    .form-container form div {
      margin-bottom: 15px;
    }
    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }
    .form-container form input, .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }
    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }
    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }
    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }
    .form-container form select {
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }
    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }
    .radio-group input[type="radio"] {
      margin-right: 5px;
    }
    .radio-group label {
      margin: 0;
    }
    .checkbox-group {
      display: flex;
      flex-wrap: wrap;
      gap: 10px;
    }
    .checkbox-group label {
      display: flex;
      align-items: center;
      gap: 5px;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <h1>ord Transfusionnel et Bilan Mensuel    </h1>

     
      <div *ngFor="let ord of OrdonnancedeSorties; let i = index">
        <h2>Ordonnance de Sortie - {{ i + 1 }}</h2>

        <!-- IdVisite-->
        <div>
          <label for="IdVisite-{{i}}">IdVisite</label>
          <input type="number" id="IdVisite-{{i}}" [(ngModel)]="ord.IdVisite" name="IdVisite-{{i}}">
        </div>

       
           <!-- medicament -->
           <div>
            <label for="NomMeducament-{{i}}">Nom Medicament:</label>
            <input type="text" id="NomMeducament-{{i}}" [(ngModel)]="ord.NomMeducament" name="NomMeducament-{{i}}">
          </div>
       
             <!-- DoseParPrise -->
             <div>
                <label for="DoseParPrise-{{i}}">Dose Par Prise:</label>
                <input type="number" id="DoseParPrise-{{i}}" [(ngModel)]="ord.DoseParPrise" name="DoseParPrise-{{i}}">
              </div>
                 <!-- RythmeJoursSemaine-->
           <div >
            <label for="RythmeJoursSemaine-{{i}}">Rythme Jours Semaine</label>
            <input type="number" id="RythmeJoursSemaine-{{i}}" [(ngModel)]="ord.RythmeJoursSemaine" name="RythmeJoursSemaine-{{i}}">
          </div>
             <!-- VoieAdministration -->
             <div >
                <label for="VoieAdministration-{{i}}">Voie Administration:</label>
                <input type="number" id="VoieAdministration-{{i}}" [(ngModel)]="ord.VoieAdministration" name="VoieAdministration-{{i}}">
              </div>
                <!-- Duree -->
             <div>
                <label for="Duree-{{i}}">Duree:</label>
                <input type="number" id="Duree-{{i}}" [(ngModel)]="ord.Duree" name="Duree-{{i}}">
              </div>
                 <!-- PostTransfusionalHb -->
             <div>
                <label for="PostTransfusionalHb-{{i}}">PostTransfusionalHb:</label>
                <input type="number" id="PostTransfusionalHb-{{i}}" [(ngModel)]="ord.PostTransfusionalHb" name="PostTransfusionalHb-{{i}}">
              </div>
                 <!-- Chh -->
             <div>
                <label for="Chh-{{i}}">Chh:</label>
                <input type="number" id="Chh-{{i}}" [(ngModel)]="ord.Chh" name="Chh-{{i}}">
              </div>
                 <!-- Rai -->
             <div>
                <label for="Rai-{{i}}">Rai:</label>
                <input type="number" id="Rai-{{i}}" [(ngModel)]="ord.Rai" name="Rai-{{i}}">
              </div>
                 <!-- ReactionTransfusionnelle -->
             <div>
                <label for="ReactionTransfusionnelle-{{i}}">ReactionTransfusionnelle:</label>
                <input type="text" id="ReactionTransfusionnelle-{{i}}" [(ngModel)]="ord.ReactionTransfusionnelle" name="ReactionTransfusionnelle-{{i}}">
              </div>
                    <!-- Ldh -->
             <div>
                <label for="Ldh-{{i}}">Ldh:</label>
                <input type="number" id="Ldh-{{i}}" [(ngModel)]="ord.Ldh" name="Ldh-{{i}}">
              </div>
                     <!-- CreatinineSerique -->
             <div>
                <label for="CreatinineSerique-{{i}}">CreatinineSerique:</label>
                <input type="number" id="CreatinineSerique-{{i}}" [(ngModel)]="ord.CreatinineSerique" name="CreatinineSerique-{{i}}">
              </div>
                     <!-- Glycemie -->
             <div>
                <label for="Glycemie-{{i}}">Glycemie:</label>
                <input type="number" id="Glycemie-{{i}}" [(ngModel)]="ord.Glycemie" name="Glycemie-{{i}}">
              </div>
                     <!-- autre exam -->
             <div>
                <label for="AutreExamen-{{i}}">AutreExamen:</label>
                <input type="text" id="AutreExamen-{{i}}" [(ngModel)]="ord.AutreExamen" name="AutreExamen-{{i}}">
              </div>
                     
        </div>

      <!-- Add ord Button -->
    
      <div class="submit-row">
        <input type="button" (click)="addCriseForm()" value="Ajouter un Bilan">
      </div>

      <!-- Submit Button -->
      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
