import { Component } from '@angular/core';
import { Sddiagnostic } from 'src/app/models/Sddiagnostic';
import { SddiagnosticService } from 'src/app/services/sddiagnostic.service';
import { HttpResponse, HttpErrorResponse } from '@angular/common/http';
import { PatientDataService } from 'src/app/services/patient-data.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-sddiagnostic',
  templateUrl: './sddiagnostic.component.html',
  styleUrls: ['./sddiagnostic.component.css']
})
export class SddiagnosticComponent {
  sddiagnostic: Sddiagnostic = new Sddiagnostic();
  patientId: string | null = null;

  constructor(private route: ActivatedRoute,private sddiagnosticService: SddiagnosticService, private patientDataService: PatientDataService // Inject the new service
  ) {}
 
  ngOnInit(): void {
    // Retrieve the PatientId from local storage
    this.patientId = localStorage.getItem('patientId');
  
    if (this.patientId && this.patientId !== '0') {
      console.log('Patient ID retrieved from local storage:', this.patientId);
    } else {
      console.error('No valid Patient ID found in local storage.');
    }
  }
  
  onSubmit() {
    // Assign the patientId from localStorage to sddiagnostic.PatientId
    if (this.patientId) {
      this.sddiagnostic.PatientId = +this.patientId;  // Ensure it's a number
    }
  
    if (this.validateSddiagnostic(this.sddiagnostic)) {
      this.sddiagnosticService.Create2(this.sddiagnostic).subscribe(
        (response: HttpResponse<any>) => {
          if (response.status === 200 || response.status === 201) {
            console.log('SD Diagnostic added successfully', response);
            alert('SD Diagnostic added successfully');
            
            // Navigate to the next page and pass the patientId
            this.router.navigate(['admin/cartT', this.sddiagnostic.PatientId]);
          } else {
            console.error('Unexpected response status', response.status);
            alert(`Unexpected response status: ${response.status}`);
          }
        },
        (error: HttpErrorResponse) => {
          console.error('Error adding SD Diagnostic', error);
          if (error.error && error.error.message) {
            alert(`Error: ${error.error.message}\nDetails: ${error.error.details}`);
          } else {
            alert('An unknown error occurred.');
          }
        }
      );
    } else {
      alert('Please fill all the required fields correctly.');
    }
  }
  

  validateSddiagnostic(sddiagnostic: Sddiagnostic): boolean {
    if (!sddiagnostic.PatientId || sddiagnostic.PatientId <= 0) {
      return false;
    }
    return true;
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>SD Diagnostic Form</title>
  <style>
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }
    .form-container form div {
      margin-bottom: 15px;
    }
    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
    }
    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }
    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }
    .form-container form input[type="submit"] {
      background-color: #000;
      color: #fff;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }
    .form-container form input[type="submit"]:hover {
      background-color: #444;
    }
    .form-container .row {
      display: flex;
      justify-content: space-between;
      margin-bottom: 15px;
    }
    .form-container .row .column {
      width: 31%;
    }
    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <h1> Circonstances du diagnostique  </h1>
      
        <div class="column">
          <label for="anemie">Anémie:</label>
          <input type="checkbox" id="anemie" [(ngModel)]="sddiagnostic.Anemie" name="anemie">
        </div>
        <div class="column">
          <label for="splenomegalie">Splénomégalie:</label>
          <input type="checkbox" id="splenomegalie" [(ngModel)]="sddiagnostic.Splenomegalie" name="splenomegalie">
        </div>
      

   
        <div class="column">
          <label for="complicationAigue">Complication Aiguë:</label>
          <input type="checkbox" id="complicationAigue" [(ngModel)]="sddiagnostic.ComplicationAigue" name="complicationAigue">
        </div>
       <!-- Additional Checkboxes -->
        <div *ngIf="sddiagnostic.ComplicationAigue" class="additional-checkboxes">
        <label>Spécification:</label>
            <input type="text" [(ngModel)]="sddiagnostic.SpecificationCa" name="Specification">
        </div>
        <div class="column">
          <label for="enqueteFamiliale">Enquête Familiale:</label>
          <input type="checkbox" id="enqueteFamiliale" [(ngModel)]="sddiagnostic.EnqueteFamiliale" name="enqueteFamiliale">
        </div>
               <!-- Additional Checkboxes -->
               <div *ngIf="sddiagnostic.EnqueteFamiliale" class="additional-checkboxes">
                <label>Spécification:</label>
                    <input type="text" [(ngModel)]="sddiagnostic.SpecificationEf" name="Specification">
                </div>
    

        <div class="column">
          <label for="age">Age:</label>
          <input type="number" id="age" [(ngModel)]="sddiagnostic.Age" name="age">
        </div>
        <div class="column">
          <label for="poids">Poids:</label>
          <input type="number" id="poids" [(ngModel)]="sddiagnostic.Poids" name="poids">
        </div>
        <div class="column">
          <label for="taille">Taille:</label>
          <input type="number" id="taille" [(ngModel)]="sddiagnostic.Taille" name="taille">
        </div>

        <div class="column">
          <label for="hemoglobine">Hémoglobine:</label>
          <input type="number" id="hemoglobine" [(ngModel)]="sddiagnostic.Hemoglobine" name="hemoglobine">
        </div>
        <div class="column">
          <label for="reticulocyte">Réticulocyte:</label>
          <input type="number" id="reticulocyte" [(ngModel)]="sddiagnostic.Reticulocyte" name="reticulocyte">
        </div>
        <div class="column">
          <label for="nbrGlobulesBlancs">Nbr Globules Blancs:</label>
          <input type="number" id="nbrGlobulesBlancs" [(ngModel)]="sddiagnostic.NbrGlobulesBlancs" name="nbrGlobulesBlancs">
        </div>

        <div class="column">
          <label for="tauxPlaquette">Taux Plaquette:</label>
          <input type="number" id="tauxPlaquette" [(ngModel)]="sddiagnostic.TauxPlaquette" name="tauxPlaquette">
        </div>
        <div class="column">
          <label for="ferritine">Ferritine:</label>
          <input type="number" id="ferritine" [(ngModel)]="sddiagnostic.Ferritine" name="ferritine">
        </div>
       
        <div class="column">
            <label for="autreComplication">Autre Complication:</label>
            <input type="checkbox" id="autreComplication" [(ngModel)]="sddiagnostic.AutreComplication" name="autreComplication">
        </div>

          <!-- Additional Checkboxes -->
          <div *ngIf="sddiagnostic.AutreComplication" class="additional-texts">
            <label>Spécification:</label>
            <input type="text" [(ngModel)]="sddiagnostic.SpecificationAutre" name="Specification">
          </div>
          <!-- electrophoreseAvantPremiereTransfusion -->
          <div class="column">
            <label for="ElectrophoreseAvantPremiereTransfusion">ElectrophoreseAvantPremiereTransfusion</label>
            <input type="checkbox" id="ElectrophoreseAvantPremiereTransfusion" [(ngModel)]="sddiagnostic.ElectrophoreseAvantPremiereTransfusion" name="ElectrophoreseAvantPremiereTransfusion">
        </div>

          <!-- Additional texts -->
          <div *ngIf="sddiagnostic.ElectrophoreseAvantPremiereTransfusion" class="additional-texts">
            <label>Hb HBA (%)</label>
            <input type="number" [(ngModel)]="sddiagnostic.HbHbA" name="HbHbA">

            <label>Hb S (%)</label>
            <input type="number" [(ngModel)]="sddiagnostic.HbS" name="HbS">

            <label>Hb A2 (%)</label>
            <input type="number" [(ngModel)]="sddiagnostic.HbA2" name="HbA2">

            <label>Hb F (%)</label>
            <input type="number" [(ngModel)]="sddiagnostic.HbF" name="HbF">

            <label>Autre Hb</label>
            <input type="text" [(ngModel)]="sddiagnostic.AutreHb" name="AutreHb">
          </div>
  

      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
