import { HttpResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Chelateur } from 'src/app/models/Chelateur';
import { TraitementChelateurReçu } from 'src/app/models/TraitementChelateurReçu';
import { ChelateurServiceService } from 'src/app/services/chelateur-service.service';
import { TreatChelateurService } from 'src/app/services/treat-chelateur.service';

@Component({
  selector: 'app-treatchelateur',
  templateUrl: './treatchelateur.component.html',
  styleUrls: ['./treatchelateur.component.css']
})
  export class TreatchelateurComponent implements OnInit {
    chelateurs: Chelateur[] = [];
    traitements: TraitementChelateurReçu[] = [];
  
    constructor(
      private treatChelateurService: TreatChelateurService,
      private chelateurService: ChelateurServiceService // Inject the Chelateur service
    ) { }
  
    ngOnInit() {
      this.loadChelateurs();
    }
  
    loadChelateurs() {
      this.chelateurService.getChelateurs().subscribe((data: Chelateur[]) => {
        this.chelateurs = data;
      });
    }
  
    onChelateurChange(event: any) {
      const chelateurId = +event.target.value;
      const isChecked = event.target.checked;
    
      if (isChecked) {
        // Initialize TraitementChelateurReçu and set ChelateurId
        const newTraitement = new TraitementChelateurReçu();
        newTraitement.ChelateurId = chelateurId;
        this.traitements.push(newTraitement);
      } else {
        // Remove the TraitementChelateur instance for the deselected ChelateurId
        this.traitements = this.traitements.filter(traitement => traitement.ChelateurId !== chelateurId);
      }
    }
    
  
    isChecked(ChelateurId: number): boolean {
      return this.traitements.some(traitement => traitement.ChelateurId === ChelateurId);
    }
  
    onSubmit() {
      this.traitements.forEach(traitement => {
        this.treatChelateurService.add(traitement).subscribe(
          (response: HttpResponse<any>) => {
            if (response.status === 200 || response.status === 201) {
              console.log('Traitement added successfully', response);
            } else {
              console.error('Unexpected response status', response.status);
            }
          },
          error => {
            console.error('Error adding Traitement', error);
          }
        );
      });
    }
  }
