import { HttpErrorResponse, HttpResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { CarteTransfusionnellePatient } from 'src/app/models/CarteTransfusionnellePatient';
import { CartetransfService } from 'src/app/services/cartetransf.service';

import { ActivatedRoute,Router } from '@angular/router';

@Component({
  selector: 'app-cartetransf',
  templateUrl: './cartetransf.component.html',
  styleUrls: ['./cartetransf.component.css']
})
export class CartetransfComponent {
  CarteTransfusionnellePatient: CarteTransfusionnellePatient = new CarteTransfusionnellePatient();
  errorMessage: string = '';
  innerExceptionMessage: string = '';
  patientId: string | null = null;

  constructor(private Cartetransf: CartetransfService,private router: Router) { }
  ngOnInit(): void {
    // Retrieve the PatientId from local storage
    this.patientId = localStorage.getItem('patientId');
  
    if (this.patientId && this.patientId !== '0') {
      console.log('Patient ID retrieved from local storage:', this.patientId);
    } else {
      console.error('No valid Patient ID found in local storage.');
    }
  }
  onSubmit() {
    if (this.patientId) {
      this.CarteTransfusionnellePatient.PatientId = +this.patientId;  // Ensure it's a number
    }
  
    if (this.validateCart(this.CarteTransfusionnellePatient)) {
      this.Cartetransf.Create2(this.CarteTransfusionnellePatient).subscribe(
        (response: HttpResponse<any>) => {
          if (response.status === 200 || response.status === 201) {
            console.log('Cart added successfully', response);
            alert('Cart added successfully');
            
            // Navigate to the next page and pass the patientId
            this.router.navigate(['admin/coeur', this.CarteTransfusionnellePatient.PatientId]);
          } else {
            console.error('Unexpected response status', response.status);
            alert(`Unexpected response status: ${response.status}`);
          }
        },
        (error: HttpErrorResponse) => {
          console.error('Error adding Cart', error);
          if (error.error && error.error.message) {
            alert(`Error: ${error.error.message}\nDetails: ${error.error.details}`);
          } else {
            alert('An unknown error occurred.');
          }
        }
      );
    } else {
      alert('Please fill all the required fields correctly.');
    }
  }
  

  validateCart(CarteTransfusionnellePatient: CarteTransfusionnellePatient): boolean {
    if (!CarteTransfusionnellePatient.PatientId || CarteTransfusionnellePatient.PatientId <= 0) {
      return false;
    }
    return true;
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Patient Form</title>
  <style>
    /* Form Container */
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    /* Form Style */
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    /* Form Input Fields */
    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize; /* Convert label text to title case */
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center; /* Center content horizontally */
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    /* Form Submit Button Hover Effect */
    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    /* Custom Select Style */
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Three Lines Per Row Layout */
    .form-container .row {
      display: flex;
      justify-content: space-between;
      margin-bottom: 15px;
    }

    .form-container .row .column {
      width: 31%;
    }

    .form-container .row .column.two-cols {
      width: 48%;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" >

      
        <div class="column">
          
          <div>
            <label for="Abo">Abo:</label>
            <select id="Abo" [(ngModel)]="CarteTransfusionnellePatient.Abo" name="Abo" required>
                <option value="A">A</option>
                <option value="B">B</option>
                <option value="O">O</option>
                <option value="AB">AB</option>

              </select>
          </div>
          <div>
            <label for="Rhesus">Rhesus:</label>
            <select id="Rhesus" [(ngModel)]="CarteTransfusionnellePatient.Rhesus" name="Rhesus" required>
                <option value="N">Negatif</option>
                <option value="P">Positif</option>
              
              </select>          
            </div>
          <div>
            <label for="PhenotypeC">PhenotypeC:</label>
            <select id="PhenotypeC" [(ngModel)]="CarteTransfusionnellePatient.PhenotypeC" name="PhenotypeC" required>
                <option value="CC">CC</option>
                <option value="Cc">Cc</option>
                <option value="cc">cc</option>

              </select>           
            </div>
          <div>
            <label for="PhenotypeE">PhenotypeE:</label>
            <select id="PhenotypeCE" [(ngModel)]="CarteTransfusionnellePatient.PhenotypeE" name="PhenotypeE" required>
                <option value="EEC">EE</option>
                <option value="Ee">Ee</option>
                <option value="ee">ee</option>

              </select> 
          </div>
          <div>
            <label for="Kell">Kell:</label>
            <select id="Kell" [(ngModel)]="CarteTransfusionnellePatient.Kell" name="Kell" required>
                <option value="N">Negatif</option>
                <option value="P">Positif</option>

              </select>           
            </div>
        </div>
        <div class="submit-row">
          <div *ngIf="errorMessage">
            <p>{{ errorMessage }}</p>
            <p *ngIf="innerExceptionMessage">{{ innerExceptionMessage }}</p>
          </div>
          <button type="submit">Submit</button>          </div>

      

    </form>
  </div>
</body>
</html>
