import { Component } from '@angular/core';
import { DashboardService } from "../dashboard.service";
import { Router } from '@angular/router';
import { UserbackService } from 'src/app/services/userback.service';
@Component({
  selector: 'top-bar',
  templateUrl: './top-bar.component.html',
})
export class TopBarComponent {
  isDropdownOpen = false;
  sidebarOpen: boolean = true; // Initially open
  constructor(private dashboard: DashboardService, private router: Router, private userbackService: UserbackService) {}

  openSidebar() {
    this.dashboard.openSidebar();
  }

  toggleDropdown() {
    this.isDropdownOpen = !this.isDropdownOpen;
  }

  logout() {
    this.userbackService.logout().subscribe(
      (response: any) => { // Specify the type of response
        console.log('Logout successful', response);
        
        // Clear the token from localStorage
        localStorage.removeItem('authToken');

        // Navigate to the login page after successful logout
        this.router.navigate(['/admin/login']); 
      },
      (error: any) => { // Specify the type of error
        console.error('Logout failed', error);
        // Handle logout error here
      }
    );
  }

  editP(){
    this.router.navigate(['/admin/editP']); 

  }
}
<header class="relative z-10 h-20 items-center">
  <div class="relative z-10 mx-auto flex h-full flex-col justify-center px-3 text-black">
    <div class="relative flex w-full items-center pl-1 sm:ml-0 sm:pr-2">
      <div class="group relative flex h-full w-12 items-center">
        <button
          type="button"
          aria-expanded="false"
          aria-label="Toggle sidebar"
          class="text-4xl text-black focus:outline-none"
          (click)="openSidebar()"
        >
          &#8801;
        </button>
      </div>
      <div class="container relative left-0 flex w-3/4">
        <div class="group relative ml-8 hidden w-full items-center md:flex lg:w-72">
          <div class="absolute flex h-10 w-auto cursor-pointer items-center justify-center p-3 pr-2 text-sm uppercase text-gray-500 sm:hidden">
            <svg
              fill="none"
              class="relative h-5 w-5"
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              
            </svg>
          </div>
          <svg
            class="pointer-events-none absolute left-0 ml-4 hidden h-4 w-4 fill-white text-gray-100 group-hover:text-gray-400 sm:block"
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 20 20"
          >
            <path
              d="M12.9 14.32a8 8 0 1 1 1.41-1.41l5.35 5.33-1.42 1.42-5.33-5.34zM8 14A6 6 0 1 0 8 2a6 6 0 0 0 0 12z"
            />
          </svg>
          
        </div>
      </div>
      <div class="relative ml-5 flex w-full items-center justify-end p-1 sm:right-auto sm:mr-0">
     

        <div class="relative">
          <a href="#" class="block" (click)="toggleDropdown()">
            <img
              alt="User Profile"
              src="./assets/images/1.jpg"
              class="mx-auto h-10 w-10 rounded-full object-cover"
            />
          </a>
          <div
            *ngIf="isDropdownOpen"
            class="absolute right-0 mt-2 w-48 bg-white border border-gray-300 rounded shadow-lg z-20"
          >
          <button
          (click)="editP()"
          class="block w-full px-4 py-2 text-left text-gray-700 hover:bg-gray-100"
        >
          Edit profile
        </button>
            <button
              (click)="logout()"
              class="block w-full px-4 py-2 text-left text-gray-700 hover:bg-gray-100"
            >
              Logout
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</header>
