import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { ComplicationFoie } from 'src/app/models/ComplicationFoie';
import { CompfoieService } from 'src/app/services/compfoie.service';

@Component({
  selector: 'app-compfoie',
  templateUrl: './compfoie.component.html',
  styleUrls: ['./compfoie.component.css']
})
export class CompfoieComponent {
  ComplicationFoie: ComplicationFoie = new ComplicationFoie();
  errorMessage: string = '';
  patientId: string | null = null;

  constructor(private compfoieService: CompfoieService) {}
  ngOnInit(): void {
    // Retrieve the PatientId from local storage
    this.patientId = localStorage.getItem('patientId');
  
    if (this.patientId && this.patientId !== '0') {
      console.log('Patient ID retrieved from local storage:', this.patientId);
    } else {
      console.error('No valid Patient ID found in local storage.');
    }
  }

  onSubmit() {  
    this.compfoieService.addFoie(this.ComplicationFoie).subscribe(
      response => {
        console.log('Complication added successfully', response);
        this.errorMessage = '';
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Complication', error);
        this.errorMessage = error.error.title;
        if (error.error.errors) {
          for (const [field, errors] of Object.entries(error.error.errors)) {
            console.error(`Validation error for ${field}:`, errors);
          }
        }
      }
    );
  }
  
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    /* Form Container */
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    /* Form Style */
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    /* Form Input Fields */
    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize; /* Convert label text to title case */
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center; /* Center content horizontally */
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    /* Form Submit Button Hover Effect */
    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }
    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    /* Custom Select Style */
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Radio Buttons Container */
    .form-container .radio-buttons {
      display: flex;
      gap: 15px; /* Space between the radio buttons */
      align-items: center; /* Vertically align items */
    }

    /* Additional Texts Style */
    .additional-texts,
    .additional-checkboxes {
      margin-top: 15px;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <h1>Complication Foie</h1>
      
     
      <div>
        <label>Hepatomegaly:</label>
        <div class="radio-buttons">
          <input type="radio" id="einsOui" [(ngModel)]="ComplicationFoie.Hepatomegaly" [value]="true" name="Hepatomegaly" >
          <label for="einsOui">Oui</label>
          <input type="radio" id="einsNon" [(ngModel)]="ComplicationFoie.Hepatomegaly" [value]="false" name="Hepatomegaly" >
          <label for="einsNon">Non</label>
        </div>
      </div>
      <div *ngIf="ComplicationFoie.Hepatomegaly" class="additional-texts">
        
        
        <div>
          <label>Préciser</label>
          <input type="text" [(ngModel)]="ComplicationFoie.Preciser" name="Preciser">
        </div>
        
      </div>
      <div>
        <label>IRM T2* Hépatique:</label>
        <div class="radio-buttons">
          <input type="radio" id="nsOui" [(ngModel)]="ComplicationFoie.Irmhepatique" [value]="true" name="Electrocardiogram" >
          <label for="nsOui">Oui</label>
          <input type="radio" id="nsNon" [(ngModel)]="ComplicationFoie.Irmhepatique" [value]="false" name="Electrocardiogram" >
          <label for="nsNon">Non</label>
        </div>
      </div>
      
      
      <div class="submit-row">
        <div *ngIf="errorMessage">
          <p style="color:red;">{{ errorMessage }}</p>
        </div>
        <input type="submit" value="Save">
      </div>
      
    </form>
  </div>
</body>
</html>
