import { HttpResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { User } from 'src/app/models/User';
import { Visite } from 'src/app/models/Visite';
import { VisiteService } from 'src/app/services/visite.service';

@Component({
  selector: 'app-visite',
  templateUrl: './visite.component.html',
  styleUrls: ['./visite.component.css']
})
export class VisiteComponent implements OnInit{
  Visite: Visite = new Visite();
  user:User[]=[];
  constructor(private VisiteService: VisiteService) { }

  private formatDateToYYYYMMDD(date: Date): string {
    return date.toISOString().split('T')[0];
  }
  ngOnInit()
  {
    this.loadUser();
  }

  loadUser() {
    this.VisiteService.GetUsers().subscribe(
      data => {
        console.log('All Users:', data); // Log all users
  
        // Filter users by roleId
        this.user = data.filter(user => {
          const roleId = Number(user.roleId); // Ensure roleId is compared as a number
          console.log('User roleId:', roleId);
          return roleId === 2; // Compare with the number 2
        });
  
        console.log('Filtered Users:', this.user); // Log filtered users
      },
      error => {
        console.error('Error fetching users:', error);
      }
    );
  }
  
  onSubmit() {
    const now = new Date();
  
    // Convertir activeUntil en format `yyyy-MM-dd` si c'est une date valide
    if (this.Visite.DateVisite) {
      this.Visite.DateVisite = this.formatDateToYYYYMMDD(new Date(this.Visite.DateVisite));
    } else {
      this.Visite.DateVisite = this.formatDateToYYYYMMDD(now); // Exemple : utiliser la date actuelle si activeUntil n'est pas définie
    }
  
    // Convertir les dates en format ISO 8601 avant de les envoyer au backend
    const VisiteToSend = {
      ...this.Visite,
      date: new Date(this.Visite.DateVisite).toISOString()
    };
  
    this.VisiteService.add(VisiteToSend).subscribe(
      (response: HttpResponse<any>) => {
        if (response.status === 200 || response.status === 201) {
          console.log('Visite added successfully', response);
        } else {
          console.error('Unexpected response status', response.status);
        }
      },
      error => {
        console.error('Error adding Visite', error);
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
  
  .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Style for CNAM radio buttons */
    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }

    .radio-group input[type="radio"] {
      margin-right: 5px;
    }

    .radio-group label {
      margin: 0;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" >
     
    
      <!-- Patient Id-->
      <div>
        <label for="PatientId">PatientId</label>
        <input type="number" id="patientId" [(ngModel)]="Visite.PatientId" name="PatientId" >
      </div>
      <div>
        <label for="UserId">Médecin:</label>
        <select id="UserId" [(ngModel)]="Visite.UserId" name="UserId" required>
          <option *ngFor="let user of user" [value]="user.userId">{{ user.nom}}{{" "}}{{ user.prenom }}</option>
        </select>
      </div>  
 <!-- Date visite-->
 <div>
    <label for="DateVisite">Date Visite</label>
    <input type="date" id="DateVisite" [(ngModel)]="Visite.DateVisite" name="UserId" >
  </div>  
   <!-- Nb visite-->
   <div>
    <label for="NbVisite">Nb Visite</label>
    <input type="number" id="NbVisite" [(ngModel)]="Visite.NbVisite" name="NbVisite" >
  </div>  


      
      <!-- Submit -->
      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
