import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { BilansPeriodiquePrevu } from 'src/app/models/BilansPeriodiquePrevu';
import { BilansPeriodiquePrevuService } from 'src/app/services/bilans-periodique-prevu.service';

@Component({
  selector: 'app-bilans-periodique-prevu',
  templateUrl: './bilans-periodique-prevu.component.html',
  styleUrls: ['./bilans-periodique-prevu.component.css']
})
export class BilansPeriodiquePrevuComponent {
  BilansPeriodiquePrevu: BilansPeriodiquePrevu = new BilansPeriodiquePrevu();
  errorMessage: string = '';

  constructor(private BilansPeriodiquePrevuService: BilansPeriodiquePrevuService) {}
 

  onSubmit() {  
    this.BilansPeriodiquePrevuService.addbp(this.BilansPeriodiquePrevu).subscribe(
      response => {
        console.log('Complication added successfully', response);
        this.errorMessage = '';
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Complication', error);
        this.errorMessage = error.error.title;
        if (error.error.errors) {
          for (const [field, errors] of Object.entries(error.error.errors)) {
            console.error(`Validation error for ${field}:`, errors);
          }
        }
      }
    );
  }
  
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
  
  .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Style for CNAM radio buttons */
    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }

    .radio-group input[type="radio"] {
      margin-right: 5px;
    }

    .radio-group label {
      margin: 0;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" >
     
      <h1>Prochain Rendez-Vous    </h1>
    
      <!-- visite Id-->
      <div>
        <label for="IdVisite">VisiteId</label>
        <input type="number" id="IdVisite" [(ngModel)]="BilansPeriodiquePrevu.IdVisite" name="IdVisite" >
      </div>
       <!-- Prochain Rendez-Vous -->
       <div>
        <label for="BilanPrevus">Bilan périodiques prévus pour la prochaine visite:</label>
        <select id="BilanPrevus" [(ngModel)]="BilansPeriodiquePrevu.BilanPrevus" name="BilanPrevus" required>
            <option value="Aucune">Bilan Mensuel</option>
            <option value="Hyperglycemie modérée à jeun">Hyperglycemie modérée à jeun</option>
            <option value="Introlerance au glucose">Introlerance au glucose </option>
            <option value="Diabète">Diabète</option>

        </select>
      </div>

 
 

      
      <!-- Submit -->
      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
