  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <h1>Complication de la drepanocytose</h1>
      <div *ngFor="let complication of complications; let i = index">
        <h2>AVC- {{ i + 1 }}</h2>
        

        <!-- Avc -->
        <div>
          <label>Avc:</label>
          <div class="radio-group">
            <input type="radio" id="AvcOui-{{i}}" [(ngModel)]="complication.avc" [value]="true" name="Avc-{{i}}">
            <label for="AvcOui-{{i}}">Oui</label>
            <input type="radio" id="AvcNon-{{i}}" [(ngModel)]="complication.avc" [value]="false" name="Avc-{{i}}">
            <label for="AvcNon-{{i}}">Non</label>
          </div>
        </div>

        <!-- Nombre -->
        <div *ngIf="complication.avc">
          <label for="Nombre-{{i}}">Nombre:</label>
          <input type="number" id="Nombre-{{i}}" [(ngModel)]="complication.nombre" name="Nombre-{{i}}">
        </div>

        <!-- Date -->
        <div *ngIf="complication.avc">
          <label for="Date-{{i}}">Date :</label>
          <input type="date" id="Date-{{i}}" [(ngModel)]="complication.date" name="Date-{{i}}">
        </div>

        <!-- Symptomatologie -->
        <div class="conditional-fields" *ngIf="complication.avc">
            <label for="Symptomatologie-{{i}}">Symptomatologie:</label>
            <div class="checkbox-group">
              <label>
                <input type="checkbox" id="Convulsions-{{i}}" [(ngModel)]="complication.convulsions" name="Convulsions-{{i}}">
                Convulsions
              </label>
              <label>
                <input type="checkbox" id="TroublesDeLaConscience-{{i}}" [(ngModel)]="complication.troublesDeLaConscience" name="TroublesDeLaConscience-{{i}}">
                Troubles De La Conscience
              </label>
              <label>
                <input type="checkbox" id="Fievre-{{i}}" [(ngModel)]="complication.fievre" name="Fievre-{{i}}">
                Fievre
              </label>
              <label>
                <input type="checkbox" id="DeficitMoteur-{{i}}" [(ngModel)]="complication.deficitMoteur" name="DeficitMoteur-{{i}}">
                Déficit Moteur
              </label>
              <label>
                <input type="checkbox" id="Autre-{{i}}" [(ngModel)]="complication.autre" name="Autre-{{i}}">
                Autre
              </label>
            </div>
          </div>
          <!-- Preciser autre -->
        <div *ngIf="complication.avc && complication.autre">
            <label for="PreciserAutre-{{i}}">Preciser Autre:</label>
            <input type="text" id="PreciserAutre-{{i}}" [(ngModel)]="complication.preciserAutre" name="PreciserAutre-{{i}}">
          </div>

           <!-- TdmCerebrale -->
        <div *ngIf="complication.avc">
            <label for="TdmCerebrale-{{i}}">TMD Cérébrale:</label>
            <input type="checkbox" id="TdmCerebrale-{{i}}" [(ngModel)]="complication.tdmCerebrale" name="TdmCerebrale-{{i}}">
          </div>
        <!-- Resultat -->
        <div *ngIf="complication.avc && complication.tdmCerebrale">
            <label for="Résultat-{{i}}">Résultat:</label>
            <input type="text" id="Résultat-{{i}}" [(ngModel)]="complication.resultatTdm" name="Résultat-{{i}}">
        </div>
           <!-- Angio IRM cérébrale -->
        <div *ngIf="complication.avc">
            <label for="AngioIrm-{{i}}">Angio IRM cérébrale:</label>
            <input type="checkbox" id="AngioIrm-{{i}}" [(ngModel)]="complication.angioIrm" name="AngioIrm-{{i}}">
          </div>
          <!-- Resultat -->
        <div *ngIf="complication.avc && complication.angioIrm">
            <label for="Résultat-{{i}}">Résultat:</label>
            <input type="text" id="Résultat-{{i}}" [(ngModel)]="complication.resultatIrm" name="Résultat-{{i}}">
        </div>
        <!-- Lieu hospitalisation -->
        <div *ngIf="complication.avc">
          <label for="LieuHospitalisation-{{i}}">Lieu Hospitalisation:</label>
          <input type="text" id="LieuHospitalisation-{{i}}" [(ngModel)]="complication.lieuHospitalisation" name="LieuHospitalisation-{{i}}">
        </div>

        <!-- Traitement -->
        <div class="conditional-fields" *ngIf="complication.avc">
          <label for="Traitement-{{i}}">Traitement:</label>
          <div class="checkbox-group">
            <label>
              <input type="checkbox" id="AntalgiquesPalliesI-{{i}}" [(ngModel)]="complication.antalgiquesPalliesI" name="AntalgiquesPalliesI-{{i}}">
              Antalgiques pallier I
            </label>
            <label>
              <input type="checkbox" id="AntalgiquePallierIi-{{i}}" [(ngModel)]="complication.antalgiquePallierIi" name="AntalgiquePallierIi-{{i}}">
              Antalgique pallier II
            </label>
            <label>
              <input type="checkbox" id="AntalgiquePallierIii-{{i}}" [(ngModel)]="complication.antalgiquePallierIii" name="AntalgiquePallierIii-{{i}}">
              Antalgique pallier III
            </label>
            <label>
              <input type="checkbox" id="HyperhydratationPerso-{{i}}" [(ngModel)]="complication.hyperhydratationPerso" name="HyperhydratationPerso-{{i}}">
              Hyperhydratation perso
            </label>
            <label>
              <input type="checkbox" id="HyperhydratationIv-{{i}}" [(ngModel)]="complication.hyperhydratationIv" name="HyperhydratationIv-{{i}}">
              Hyperhydratation IV
            </label>
            <label>
              <input type="checkbox" id="TransfusionSimple-{{i}}" [(ngModel)]="complication.transfusionSimple" name="TransfusionSimple-{{i}}">
              Transfusion simple
            </label>
            <label>
              <input type="checkbox" id="EchangeTransfusionnel-{{i}}" [(ngModel)]="complication.echangeTransfusionnel" name="EchangeTransfusionnel-{{i}}">
              Echange transfusionnel
            </label>
            <label>
                <input type="checkbox" id="AutreTraitement-{{i}}" [(ngModel)]="complication.autreTraitement" name="AutreTraitement-{{i}}">
                Autre
            </label>
            <div *ngIf="complication.avc && complication.autreTraitement">
                <label for="PreciserAutreTraitement-{{i}}">Preciser:</label>
                <input type="text" id="PreciserAutreTraitement-{{i}}" [(ngModel)]="complication.preciserAutreTraitement" name="PreciserAutreTraitement-{{i}}">
            </div>
          </div>
        </div>
  <!-- Sequelles -->
  <div *ngIf="complication.avc">
    <label for="Sequelles-{{i}}">Sequelles:</label>
    <input type="checkbox" id="Sequelles-{{i}}" [(ngModel)]="complication.sequelles" name="Sequelles-{{i}}">
  </div>
      </div>
      
      <div class="submit-row">
        <input type="button" (click)="addComplication()" value="Ajouter Crise AVC">
      </div>
      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
