import { HttpResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { PerscriptionMedicale } from 'src/app/models/PerscriptionMedicale';
import { PrescriptionService } from 'src/app/services/prescription.service';

@Component({
  selector: 'app-prescription',
  templateUrl: './prescription.component.html',
  styleUrls: ['./prescription.component.css']
})
export class PrescriptionComponent {
  PerscriptionMedicale: PerscriptionMedicale = new PerscriptionMedicale();

  constructor(private PrescriptionService : PrescriptionService) { }

  

  onSubmit() {
    const now = new Date();
  
   
  
    this.PrescriptionService.add(this.PerscriptionMedicale).subscribe(
      (response: HttpResponse<any>) => {
        if (response.status === 200 || response.status === 201) {
          console.log('Visite added successfully', response);
        } else {
          console.error('Unexpected response status', response.status);
        }
      },
      error => {
        console.error('Error adding Visite', error);
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
  
  .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Style for CNAM radio buttons */
    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }

    .radio-group input[type="radio"] {
      margin-right: 5px;
    }

    .radio-group label {
      margin: 0;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" >
     
      <h1>Anomalie Glucidique</h1>
    
      <!-- Visite Id-->
      <div>
        <label for="IdVisite">IdVisite</label>
        <input type="number" id="IdVisite" [(ngModel)]="PerscriptionMedicale.IdVisite" name="IdVisite" >
      </div>
         
 
      <div>
        <label for="BilanVisite">Le bilan périodique à prélever lors de cette visite:</label>
        <select id="BilanVisite" [(ngModel)]="PerscriptionMedicale.BilanVisite" name="BilanVisite" >
          <option value="Bilan mensuel">Bilan mensuel</option>
          <option value="Bilan trimestriel">Bilan trimestriel/option>
          <option value="Bilan semestriel">Bilan semestriel </option>
          <option value="Bilan annuel">Bilan annuel </option>

        </select>
      </div>
      <!-- autre bilan-->
   <div>
    <label for="AutreBilan">Autre bilan à prélever lors de cette visite </label>
    <input type="text" id="AutreBilan" [(ngModel)]="PerscriptionMedicale.AutreBilan" name="AutreBilan" >
  </div>  
   <!-- type produit sanguin-->
   <div>
    <label for="TypeProduitSanguin">type de produit sanguin : </label>
    <input type="text" id="TypeProduitSanguin" [(ngModel)]="PerscriptionMedicale.TypeProduitSanguin" name="TypeProduitSanguin" value="CGR">
</div>  
<!-- autre bilan-->
<div>
    <label for="autretypeProduitSanguin">autre type de Produit Sanguin </label>
    <input type="text" id="autretypeProduitSanguin" [(ngModel)]="PerscriptionMedicale.AutretypeProduitSanguin" name="autretypeProduitSanguin" >
  </div>  
<!-- filtré-->
<div>
    <label for="QualitéSangFiltre">filtré </label>
    <input type="checkbox" id="QualitéSangFiltre" [(ngModel)]="PerscriptionMedicale.QualiteSangFiltre" name="QualitéSangFiltre" >
  </div>  

  <!-- Lavé-->
<div>
    <label for="Lavé">Lavé </label>
    <input type="checkbox" id="Lavé" [(ngModel)]="PerscriptionMedicale.QualiteSangLave" name="Lavé" >
  </div>  
  <!-- Irradié-->
<div>
    <label for="Irradié">Irradié </label>
    <input type="checkbox" id="Irradié" [(ngModel)]="PerscriptionMedicale.QualiteSangIrradie" name="Irradié" >
  </div>  
   <!-- Irradié-->
<div>
    <label for="Quantite">Quantité du Sang (ml) </label>
    <input type="number" id="Quantite" [(ngModel)]="PerscriptionMedicale.Quantite" name="Quantite" >
  </div>      
  <!--Durée prévue de la transfusion (heurs)-->
  <div>
    <label for="Durree">  Durée prévue de la transfusion (heurs) :    </label>
    <input type="number" id="Durree" [(ngModel)]="PerscriptionMedicale.Durree" name="Durree" >
  </div> 
  <!-- autre préscription-->
<div>
    <label for="AutrePrescription">autre préscription </label>
    <input type="text" id="AutrePrescription" [(ngModel)]="PerscriptionMedicale.AutrePrescription" name="AutrePrescription" >
  </div>  
      <!-- Submit -->
      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
