import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { IrmFoie } from 'src/app/models/IrmFoie';
import { IrmfoieService } from 'src/app/services/irmfoie.service';
import { Observable, forkJoin } from 'rxjs';

@Component({
  selector: 'app-updateirmf',
  templateUrl: './updateirmf.component.html',
  styleUrls: ['./updateirmf.component.css']
})
export class UpdateirmfComponent {
  complications: any[] = [];
  errorMessage: string = '';

  constructor(
    private IrmfoieService: IrmfoieService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const PatientId = params.get('PatientId');
      if (PatientId) {
        const id = parseInt(PatientId, 10);
        if (!isNaN(id)) {
          this.getById(id);
        } else {
          console.error('Invalid patient ID:', PatientId);
        }
      } else {
        console.error('Patient ID is missing in route');
      }
    });
  
  }

  private getById(PatientId: number) {
    this.IrmfoieService.getById(PatientId).subscribe(
      (data: any) => {
        // Ensure data is an array
        if (Array.isArray(data)) {
          this.complications = data;
        } else {
          this.complications = [data]; // Convert to array if single object is returned
        }
        console.log('Formatted data:', this.complications);
      },
      (error: HttpErrorResponse) => {
        this.errorMessage = error.message;
        console.error('Error fetching complication data:', error);
      }
    );
  }

  onSubmit() {
    const PatientId = parseInt(this.route.snapshot.paramMap.get('PatientId')!, 10);
  
    if (!isNaN(PatientId)) {
      // Iterate through each crisis and update it
      const updateObservables: Observable<any>[] = this.complications.map(complication => {
        // Ensure the crisis object conforms to the expected model
        const compData: IrmFoie= {
          ...complication,
          PatientId: PatientId // Ensure the patient ID is included
        };
  
        return this.IrmfoieService.update(PatientId, compData);
      });
  
      // Execute all update observables in parallel
      forkJoin(updateObservables).subscribe(
        responses => {
          console.log('All updates successful', responses);
         // this.router.navigate(['/']); // Adjust this to your desired route after successful update
        },
        (error: HttpErrorResponse) => {
          this.errorMessage = error.message;
          console.error('Error updating complication data:', error);
        }
      );
    } else {
      console.error('Invalid patient ID:', PatientId);
    }
  }
  
  
 

  addComplication() {
    this.complications.push({});
  }
}

<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container h2 {
      text-align: center;
      font-size: 30px;
      font-weight: bold;
      color: #c95355;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #f12349;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form .form-row {
      display: flex;
      gap: 20px; /* Adjust gap as needed */
      flex-wrap: wrap;
    }

    .form-container form .form-row div {
      flex: 1;
      min-width: 200px; /* Adjust min-width as needed */
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Style for CNAM radio buttons */
    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }

    .radio-group input[type="radio"] {
      margin-right: 5px;
    }

    .radio-group label {
      margin: 0;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <h1>Complication Foie</h1>
      <br>
      <div *ngFor="let IrmFoie of complications; let i = index">
        <h2>Irm Foie - {{ i + 1 }}</h2>

        <!-- Date Irm -->
        <div class="form-row">
          <div>
            <label for="dateirm-{{i}}">Date Irm:</label>
            <input type="date" id="dateirm-{{i}}" [(ngModel)]="IrmFoie.dateIrm" name="dateirm-{{i}}" required>
          </div>

          <!-- Ct2 -->
          <div>
            <label for="ft2-{{i}}">Ft2:</label>
            <input type="number" id="ft2-{{i}}" [(ngModel)]="IrmFoie.ft2" name="ft2-{{i}}" required>
          </div>

          <!-- Concentration HepatiqueFer -->
          <div>
            <label for="ConcentrationHepatiqueFer-{{i}}">Concentration Hepatique Fer:</label>
            <input type="number" id="ConcentrationHepatiqueFer-{{i}}" [(ngModel)]="IrmFoie.concentrationHepatiqueFer" name="ConcentrationHepatiqueFer-{{i}}" required>
          </div>

          <!-- Fr2 -->
          <div>
            <label for="fr2-{{i}}">Fr2:</label>
            <input type="number" id="fr2-{{i}}" [(ngModel)]="IrmFoie.fr2" name="fr2-{{i}}" required>
          </div>
        </div>
      </div>
        <div class="submit-row">
          <input type="button" (click)="addComplication()" value="Ajouter Crise">
        </div>
      
      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
