import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { CarnetBilanMensuel } from 'src/app/models/CarnetBilanMensuel';
import { CarnetBilanMensuelService } from 'src/app/services/carnet-bilan-mensuel.service';

@Component({
  selector: 'app-carnet-bilan-mensuel',
  templateUrl: './carnet-bilan-mensuel.component.html',
  styleUrls: ['./carnet-bilan-mensuel.component.css']
})
export class CarnetBilanMensuelComponent {
  carnets: CarnetBilanMensuel[] = [new CarnetBilanMensuel()];
  errorMessage: string = '';

  constructor(private CarnetBilanMensuelService: CarnetBilanMensuelService) {}

  private formatDateToYYYYMMDD(date: Date): string {
    return date.toISOString().split('T')[0];
  }

  addCriseForm() {
    this.carnets.push(new CarnetBilanMensuel());
  }

  onSubmit() {
    const now = new Date();

    this.carnets.forEach(carnet => {
      carnet.Date = carnet.Date ? this.formatDateToYYYYMMDD(new Date(carnet.Date)) : this.formatDateToYYYYMMDD(now);

      // Convertir les dates en format ISO 8601 avant de les envoyer au backend
      const complicationToSend = {
        ...carnet,
        DateEchocardiographie: new Date(carnet.Date).toISOString(),
      };

      this.CarnetBilanMensuelService.addcarnet(complicationToSend).subscribe(
        response => {
          console.log('Complication added successfully', response);
          this.errorMessage = '';
        },
        (error: HttpErrorResponse) => {
          console.error('Error adding complication', error);
          this.errorMessage = error.error.title;
          if (error.error.errors) {
            for (const [field, errors] of Object.entries(error.error.errors)) {
              console.error(`Validation error for ${field}:`, errors);
            }
          }
        }
      );
    });
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }
    /* Style for Add Crise Button */
.add-crise-button {
  background-color: #3498db;
  color: #fff;
  padding: 12px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease, transform 0.3s ease;
  font-size: 16px;
  font-weight: bold;
}

.add-crise-button:hover {
  background-color: #2980b9;
  transform: scale(1.05);
}

.add-crise-button:focus {
  outline: none;
}


    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }

    .radio-group input[type="radio"] {
      margin-right: 5px;
    }

    .radio-group label {
      margin: 0;
    }

    .conditional-fields {
      margin-top: 15px;
    }

    .checkbox-group {
      display: flex;
      flex-wrap: wrap;
      gap: 20px;
    }

    .form-container h2 {
      text-align: center;
      font-size: 30px;
      font-weight: bold;
      color: #c95355;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #f12349;
      padding-bottom: 10px;
      display: inline-block;
    }
    .checkbox-group div {
      display: flex;
      align-items: center;
      gap: 5px;
    }

  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <h1>Carnet Transfusionnel et Bilan Mensuel    </h1>

     
      <div *ngFor="let carnet of carnets; let i = index">
        <h2>Carnet - {{ i + 1 }}</h2>

        <!-- IdVisite-->
        <div>
          <label for="IdVisite-{{i}}">IdVisite</label>
          <input type="number" id="IdVisite-{{i}}" [(ngModel)]="carnet.IdVisite" name="IdVisite-{{i}}">
        </div>

        
        
     

        <!-- Date -->
        <div >
          <label for="Date-{{i}}">Date :</label>
          <input type="date" id="Date-{{i}}" [(ngModel)]="carnet.Date" name="Date-{{i}}">
        </div>
           <!-- volume transfused -->
           <div>
            <label for="VolumeTransfused-{{i}}">VolumeTransfused:</label>
            <input type="number" id="VolumeTransfused-{{i}}" [(ngModel)]="carnet.VolumeTransfused" name="VolumeTransfused-{{i}}">
          </div>
       
             <!-- Hb -->
             <div>
                <label for="Hb-{{i}}">Hb:</label>
                <input type="number" id="Hb-{{i}}" [(ngModel)]="carnet.Hb" name="Hb-{{i}}">
              </div>
                 <!-- plaquette-->
           <div >
            <label for="Plaquette-{{i}}">Plaquette</label>
            <input type="number" id="Plaquette-{{i}}" [(ngModel)]="carnet.Plaquette" name="Plaquette-{{i}}">
          </div>
             <!-- pnn -->
             <div >
                <label for="Pnn-{{i}}">Pnn:</label>
                <input type="number" id="Pnn-{{i}}" [(ngModel)]="carnet.Pnn" name="Pnn-{{i}}">
              </div>
                <!-- Reticulocytes -->
             <div>
                <label for="Reticulocytes-{{i}}">Reticulocytes:</label>
                <input type="number" id="Reticulocytes-{{i}}" [(ngModel)]="carnet.Reticulocytes" name="Reticulocytes-{{i}}">
              </div>
                 <!-- PostTransfusionalHb -->
             <div>
                <label for="PostTransfusionalHb-{{i}}">PostTransfusionalHb:</label>
                <input type="number" id="PostTransfusionalHb-{{i}}" [(ngModel)]="carnet.PostTransfusionalHb" name="PostTransfusionalHb-{{i}}">
              </div>
                 <!-- Chh -->
             <div>
                <label for="Chh-{{i}}">Chh:</label>
                <input type="number" id="Chh-{{i}}" [(ngModel)]="carnet.Chh" name="Chh-{{i}}">
              </div>
                 <!-- Rai -->
             <div>
                <label for="Rai-{{i}}">Rai:</label>
                <input type="number" id="Rai-{{i}}" [(ngModel)]="carnet.Rai" name="Rai-{{i}}">
              </div>
                 <!-- ReactionTransfusionnelle -->
             <div>
                <label for="ReactionTransfusionnelle-{{i}}">ReactionTransfusionnelle:</label>
                <input type="text" id="ReactionTransfusionnelle-{{i}}" [(ngModel)]="carnet.ReactionTransfusionnelle" name="ReactionTransfusionnelle-{{i}}">
              </div>
                    <!-- Ldh -->
             <div>
                <label for="Ldh-{{i}}">Ldh:</label>
                <input type="number" id="Ldh-{{i}}" [(ngModel)]="carnet.Ldh" name="Ldh-{{i}}">
              </div>
                     <!-- CreatinineSerique -->
             <div>
                <label for="CreatinineSerique-{{i}}">CreatinineSerique:</label>
                <input type="number" id="CreatinineSerique-{{i}}" [(ngModel)]="carnet.CreatinineSerique" name="CreatinineSerique-{{i}}">
              </div>
                     <!-- Glycemie -->
             <div>
                <label for="Glycemie-{{i}}">Glycemie:</label>
                <input type="number" id="Glycemie-{{i}}" [(ngModel)]="carnet.Glycemie" name="Glycemie-{{i}}">
              </div>
                     <!-- autre exam -->
             <div>
                <label for="AutreExamen-{{i}}">AutreExamen:</label>
                <input type="text" id="AutreExamen-{{i}}" [(ngModel)]="carnet.AutreExamen" name="AutreExamen-{{i}}">
              </div>
                     
        </div>

      <!-- Add carnet Button -->
      <div class="submit-row">
        <button class="add-carnet-button" type="button" (click)="addCriseForm()">Ajouter un bialn</button>
      </div>

      <!-- Submit Button -->
      <div class="submit-row">
        <input type="submit" value="Submit">
      </div>
    </form>
  </div>
</body>
</html>
