import { Component } from '@angular/core';
import { User } from 'src/app/models/User';
import { UserbackService } from 'src/app/services/userback.service';

@Component({
  selector: 'app-status',
  templateUrl: './status.component.html',
  styleUrls: ['./status.component.css']
})
export class StatusComponent {
  user: User = new User();

  constructor(private userbackService: UserbackService) { }

  onSubmit() {
    const now = new Date();
    this.user.dateCreation = now.toISOString();  // Convertir la date actuelle en chaîne ISO 8601

    // Convertir activeUntil en chaîne ISO 8601 si c'est une date valide
    if (this.user.activeUntil) {
      this.user.activeUntil = new Date(this.user.activeUntil).toISOString();
    } else {
      this.user.activeUntil = now.toISOString(); // Exemple : utiliser la date actuelle si activeUntil n'est pas définie
    }
    this.userbackService.addUser(this.user).subscribe(
      response => {
        console.log('User added successfully', response);
      },
      error => {
        console.error('Error adding user', error);
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>User Form</title>
  <style>
    /* Form Container */
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    /* Form Style */
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    /* Form Input Fields */
    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize; /* Convert label text to title case */
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center; /* Center content horizontally */
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    /* Form Submit Button Hover Effect */
    .form-container form input[type="submit"]:hover {
      background-color: #000;
      color: #fff;
    }

    /* Custom Select Style */
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Three Lines Per Row Layout */
    .form-container .row {
      display: flex;
      justify-content: space-between;
      margin-bottom: 15px;
    }

    .form-container .row .column {
      width: 31%;
    }

    .form-container .row .column.two-cols {
      width: 48%;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" action="/submit-user" >

      <!-- First Row -->
      <div class="row">
        <!-- Left Column -->
        <div class="column">
          <div>
            <label for="userName">Username:</label>
            <input type="text" id="userName" [(ngModel)]="user.userName" name="userName" pattern="[A-Za-z ]{3,20}" title="Username must be letters only, with spaces allowed, and between 3 to 20 characters long." required>
          </div>

          <div>
            <label for="password">Password:</label>
            <input type="password" id="password" [(ngModel)]="user.password" name="password" required>
          </div>
        </div>

        <!-- Middle Column -->
        <div class="column">
          <div class="row">
            <div class="column two-cols">
              <label for="active">Active:</label>
              <input type="checkbox" id="active" [(ngModel)]="user.active" name="active" checked>
            </div>

            <div class="column two-cols">
              <label for="recycleBin">Recycle Bin:</label>
              <input type="checkbox" id="recycleBin" [(ngModel)]="user.recycleBin" name="recycleBin">
            </div>
          </div>

          <div class="row">
            <div class="column two-cols">
              <label for="activeUntil">Active Until:</label>
              <input type="date" id="activeUntil" [(ngModel)]="user.activeUntil"name="activeUntil">
            </div>

            <div class="column two-cols">
              <label for="dateCreation">Date Creation:</label>
              <input type="date" id="dateCreation" [(ngModel)]="user.dateCreation"name="dateCreation" required>
            </div>
          </div>
        </div>

        <!-- Right Column -->
        <div class="column">
          <div>
            <label for="mobile">Mobile:</label>
            <input type="text" id="mobile" [(ngModel)]="user.mobile" name="mobile">
          </div>
          <div>
            <label for="roleId">RoleID:</label>
            <input type="text" id="roleId" [(ngModel)]="user.roleId" name="roleId">
          </div>
          <div>
            <label for="role">Role:</label>
            <select id="role" name="role" required>
              <option value="">Select Role</option>
              <option value="1">Admin</option>
              <option value="2">Medecin</option>
              <option value="3">Infirmier</option>
              <option value="4">Pharmacien</option>
            </select>
          </div>
        </div>
      </div>

      <!-- Second Row (Nom, Prenom, Email in one line) -->
      <div class="row">
        <div class="column">
          <label for="nom">Nom:</label>
          <input type="text" id="nom" [(ngModel)]="user.nom"name="nom" required>
        </div>
        <div class="column">
          <label for="prenom">Prénom:</label>
          <input type="text" id="prenom" [(ngModel)]="user.prenom"name="prenom" required>
        </div>
        <div class="column">
          <label for="email">Email:</label>
          <input type="email" id="email" [(ngModel)]="user.email" name="email">
        </div>
      </div>

      <!-- Third Row (Submit Button) -->
      <div class="row">
        <div class="column" style="width: 100%; text-align: center;"> <!-- Adjusted width and centered text-align -->
            <div class="submit-row">
            <input type="submit" value="Submit">
          </div>
        </div>
      </div>
    </form>
  </div>
</body>
</html>
