import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { IrmCoeur } from 'src/app/models/IrmCoeur';
import { IrmcoeurService } from 'src/app/services/irmcoeur.service';

@Component({
  selector: 'app-irmcoeur',
  templateUrl: './irmcoeur.component.html',
  styleUrls: ['./irmcoeur.component.css']
})
export class IrmcoeurComponent {
  IrmCoeur: IrmCoeur = new IrmCoeur();
  errorMessage: string = '';

  constructor(private coeurService: IrmcoeurService) {}
  private formatDateToYYYYMMDD(date: Date): string {
    return date.toISOString().split('T')[0];
  }

  onSubmit() {
    const now = new Date();
    this.IrmCoeur.DateIrm = this.formatDateToYYYYMMDD(now);  // Format `yyyy-MM-dd`
  
    if (this.IrmCoeur.DateIrm) {
      this.IrmCoeur.DateIrm = this.formatDateToYYYYMMDD(new Date(this.IrmCoeur.DateIrm));
    } else {
      this.IrmCoeur.DateIrm = this.formatDateToYYYYMMDD(now); 
    }
  
    // Convertir les dates en format ISO 8601 avant de les envoyer au backend
    const complicationToSend = {
      ...this.IrmCoeur,
      DateEchocardiographie: new Date(this.IrmCoeur.DateIrm).toISOString(),
    };
  
    
    this.coeurService.addIrm(complicationToSend).subscribe(
      response => {
        console.log('IrmCoeur added successfully', response);
        this.errorMessage = '';
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Irm', error);
        this.errorMessage = error.error.title;
        if (error.error.errors) {
          for (const [field, errors] of Object.entries(error.error.errors)) {
            console.error(`Validation error for ${field}:`, errors);
          }
        }
      }
    );
  }
}
