import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { ComplicationGlucidique } from 'src/app/models/ComplicationGlucidique';
import { GlucidiqueService } from 'src/app/services/glucidique.service';

@Component({
  selector: 'app-updateglucidique',
  templateUrl: './updateglucidique.component.html',
  styleUrls: ['./updateglucidique.component.css']
})
export class UpdateglucidiqueComponent {
  ComplicationGlucidique: any= {};
  errorMessage: string = '';

  constructor(
    private GlucidiqueService: GlucidiqueService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  
  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const PatientID = params.get('PatientID');
      if (PatientID) {
        const id = parseInt(PatientID, 10);
        if (!isNaN(id)) {
          this.getComplicationglucidiqueById(id);
        } else {
          console.error('Invalid patient ID:', PatientID);
        }
      } else {
        console.error('Patient ID is missing in route');
      }
    });
  }
  
  
  private getComplicationglucidiqueById(id: number) {
    this.GlucidiqueService.getComplicationglucidiqueById(id).subscribe(
      (data: ComplicationGlucidique) => {
        this.ComplicationGlucidique = data;
        // Optionally log the data to ensure it's correctly set
        console.log('Formatted data:', this.ComplicationGlucidique);
      },
      (error: HttpErrorResponse) => {
        this.errorMessage = error.message;
        console.error('Error fetching complication data:', error);
      }
    );
  }

  
  

  onSubmit() {
    const patientID = parseInt(this.route.snapshot.paramMap.get('PatientID')!, 10);
    if (!isNaN(patientID)) {
        this.GlucidiqueService.update(patientID, this.ComplicationGlucidique).subscribe(
            response => {
                console.log('Update successful', response);
                //this.router.navigate(['/']); // Adjust this to your desired route after successful update
            },
            (error: HttpErrorResponse) => {
                this.errorMessage = error.message;
                console.error('Error updating complication data:', error);
            }
        );
    } else {
        console.error('Invalid patient ID:', patientID);
    }
}

}
