import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ComplicationCoeur } from 'src/app/models/ComplicationCoeur';
import { CoeurserviceService } from 'src/app/services/coeurservice.service';

@Component({
  selector: 'app-updatecompcoeur',
  templateUrl: './updatecompcoeur.component.html',
  styleUrls: ['./updatecompcoeur.component.css']
})
export class UpdatecompcoeurComponent {
  ComplicationCoeur: any= {};
  errorMessage: string = '';

  constructor(
    private coeurService: CoeurserviceService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const PatientID = params.get('PatientID');
      if (PatientID) {
        const id = parseInt(PatientID, 10);
        if (!isNaN(id)) {
          this.getComplicationCoeurById(id);
        } else {
          console.error('Invalid patient ID:', PatientID);
        }
      } else {
        console.error('Patient ID is missing in route');
      }
    });
  }
  
  private getComplicationCoeurById(id: number) {
    this.coeurService.getComplicationCoeurById(id).subscribe(
      (data: ComplicationCoeur) => {
        this.ComplicationCoeur = data;
        // Optionally log the data to ensure it's correctly set
        console.log('Formatted data:', this.ComplicationCoeur);
        if (this.ComplicationCoeur && this.ComplicationCoeur.DateEchocardiographie) {
          this.ComplicationCoeur.DateEchocardiographie = this.formatDate(this.ComplicationCoeur.DateEchocardiographie);
        }
      },
      (error: HttpErrorResponse) => {
        this.errorMessage = error.message;
        console.error('Error fetching complication data:', error);
      }
    );
  }
  
  private formatDate(date: string): string {
    const d = new Date(date);
    return d.toISOString().split('T')[0]; // Format to 'yyyy-MM-dd'
  }
  
  

  onSubmit() {
    const patientID = parseInt(this.route.snapshot.paramMap.get('PatientID')!, 10);
    if (!isNaN(patientID)) {
        this.coeurService.update(patientID, this.ComplicationCoeur).subscribe(
            response => {
                console.log('Update successful', response);
                //this.router.navigate(['/']); // Adjust this to your desired route after successful update
            },
            (error: HttpErrorResponse) => {
                this.errorMessage = error.message;
                console.error('Error updating complication data:', error);
            }
        );
    } else {
        console.error('Invalid patient ID:', patientID);
    }
}

}
