import { Component } from '@angular/core';
import { ComplicationCoeur } from 'src/app/models/ComplicationCoeur';
import { CoeurserviceService } from 'src/app/services/coeurservice.service';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-complicationcoeur',
  templateUrl: './complicationcoeur.component.html',
  styleUrls: ['./complicationcoeur.component.css']
})
export class ComplicationcoeurComponent {
  ComplicationCoeur: ComplicationCoeur = new ComplicationCoeur();
  errorMessage: string = '';

  constructor(private coeurService: CoeurserviceService) {}
  private formatDateToYYYYMMDD(date: Date): string {
    return date.toISOString().split('T')[0];
  }

  onSubmit() {
    const now = new Date();
    this.ComplicationCoeur.DateEchocardiographie = this.formatDateToYYYYMMDD(now);  // Format `yyyy-MM-dd`
  
    if (this.ComplicationCoeur.DateEchocardiographie) {
      this.ComplicationCoeur.DateEchocardiographie = this.formatDateToYYYYMMDD(new Date(this.ComplicationCoeur.DateEchocardiographie));
    } else {
      this.ComplicationCoeur.DateEchocardiographie = this.formatDateToYYYYMMDD(now); 
    }
  
    // Convertir les dates en format ISO 8601 avant de les envoyer au backend
    const complicationToSend = {
      ...this.ComplicationCoeur,
      DateEchocardiographie: new Date(this.ComplicationCoeur.DateEchocardiographie).toISOString(),
    };
  
    
    this.coeurService.addcompcoeur(complicationToSend).subscribe(
      response => {
        console.log('Complication added successfully', response);
        this.errorMessage = '';
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Complication', error);
        this.errorMessage = error.error.title;
        if (error.error.errors) {
          for (const [field, errors] of Object.entries(error.error.errors)) {
            console.error(`Validation error for ${field}:`, errors);
          }
        }
      }
    );
  }
  
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Patient Form</title>
  <style>
    /* Form Container */
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    /* Form Style */
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    /* Form Input Fields */
    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize; /* Convert label text to title case */
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center; /* Center content horizontally */
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    /* Form Submit Button Hover Effect */
    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }
    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    /* Custom Select Style */
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Radio Buttons Container */
    .form-container .radio-buttons {
      display: flex;
      gap: 15px; /* Space between the radio buttons */
      align-items: center; /* Vertically align items */
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <h1>Complication Coeur</h1>
      <div>
        <label for="PatientId">PatientId</label>
        <input type="number" id="patientId" [(ngModel)]="ComplicationCoeur.PatientId" name="PatientId" >
      </div>
      <div>
        <label>Insuffaisance cardiaque:</label>
        <div class="radio-buttons">
          <input type="radio" id="insOui" [(ngModel)]="ComplicationCoeur.ArretCardiaque" [value]="true" name="ArretCardiaque" >
          <label for="insOui">Oui</label>
          <input type="radio" id="insNon" [(ngModel)]="ComplicationCoeur.ArretCardiaque" [value]="false" name="ArretCardiaque" >
          <label for="insNon">Non</label>
        </div>
      </div>
      <div>
        <label>Echocardiographie:</label>
        <div class="radio-buttons">
          <input type="radio" id="einsOui" [(ngModel)]="ComplicationCoeur.Echocardiographie" [value]="true" name="Echocardiographie" >
          <label for="einsOui">Oui</label>
          <input type="radio" id="einsNon" [(ngModel)]="ComplicationCoeur.Echocardiographie" [value]="false" name="Echocardiographie" >
          <label for="einsNon">Non</label>
        </div>
      </div>
      <div *ngIf="ComplicationCoeur.Echocardiographie" class="additional-texts">
        <div>
          <label>Date Echocardiographie</label>
          <input type="date" [(ngModel)]="ComplicationCoeur.DateEchocardiographie" name="DateEchocardiographie">
        </div>
        <div>
          <label>Signes Cardiomyopathy</label>
          <input type="checkbox" [(ngModel)]="ComplicationCoeur.SignesCardiomypathy" name="SignesCardiomypathy">
        </div>
        <div>
          <label>Fraction d'ejection systolique ventriculaire gauche (%)</label>
          <input type="number" [(ngModel)]="ComplicationCoeur.FractionEjectionSystoliqueGauche" name="FractionEjectionSystoliqueGauche">
        </div>
        <div>
          <label>Signes HTAP</label>
          <input type="checkbox" [(ngModel)]="ComplicationCoeur.SignesHtap" name="SignesHtap" value="true">
        </div>
      </div>
      <div>
        <label>Electrocardiogram:</label>
        <div class="radio-buttons">
          <input type="radio" id="nsOui" [(ngModel)]="ComplicationCoeur.Electrocardiogram" [value]="true" name="Electrocardiogram" >
          <label for="nsOui">Oui</label>
          <input type="radio" id="nsNon" [(ngModel)]="ComplicationCoeur.Electrocardiogram" [value]="false" name="Electrocardiogram" >
          <label for="nsNon">Non</label>
        </div>
      </div>
      <div *ngIf="ComplicationCoeur.Electrocardiogram" class="additional-checkboxes">
        <label>Presence Anomalie:</label>
        <div class="radio-buttons">
          <input type="radio" id="anomalieOui" [(ngModel)]="ComplicationCoeur.AutresAnomaliesEchographiques" [value]="true" name="AutresAnomaliesEchographiques" >
          <label for="anomalieOui">Oui</label>
          <input type="radio" id="anomalieNon" [(ngModel)]="ComplicationCoeur.AutresAnomaliesEchographiques" [value]="false" name="AutresAnomaliesEchographiques" >
          <label for="anomalieNon">Non</label>
        </div>
      </div>
      <div>
        <label>Autre explorations:</label>
        <div class="radio-buttons">
          <input type="radio" id="nOui" [(ngModel)]="ComplicationCoeur.ExplorationsCardiaquesAutre" [value]="true" name="ExplorationsCardiaquesAutre" >
          <label for="nOui">Oui</label>
          <input type="radio" id="nNon" [(ngModel)]="ComplicationCoeur.ExplorationsCardiaquesAutre" [value]="false" name="ExplorationsCardiaquesAutre" >
          <label for="nNon">Non</label>
        </div>
      </div>
      
      <div>
        <label>IRM Cardiaque:</label>
        <div class="radio-buttons">
          <input type="radio" id="irmOui" [(ngModel)]="ComplicationCoeur.Irmcardiaque" [value]="true" name="IrmCardiaque" >
          <label for="irmOui">Oui</label>
          <input type="radio" id="irmNon" [(ngModel)]="ComplicationCoeur.Irmcardiaque" [value]="false" name="IrmCardiaque" >
          <label for="irmNon">Non</label>
        </div>
      </div>
      <div class="submit-row">
        <div *ngIf="errorMessage">
          <p style="color:red;">{{ errorMessage }}</p>
        </div>
        <input type="submit" value="Save">
      </div>
      
    </form>
  </div>
</body>
</html>
