import { Component, OnInit } from '@angular/core';
import { Patient } from 'src/app/models/Patient';
import { HttpResponse } from '@angular/common/http';
import { PatientbackService } from 'src/app/services/patientback.service';
import { CategorieMaladie } from 'src/app/models/CategorieMaladie';
import { NiveauEducation } from 'src/app/models/NiveauEducation';

@Component({
  selector: 'app-archive',
  templateUrl: './archive.component.html',
  styleUrls: ['./archive.component.css']
})
export class ArchiveComponent  implements OnInit{
  patient: Patient = new Patient();
  niveauxEducation: NiveauEducation[] = [];
  categoriesMaladie: CategorieMaladie[] = [];

  constructor(private patientService: PatientbackService) {}

  ngOnInit() {
    this.loadNiveauEducation();
    this.loadCategorieMaladie();
  }

  loadNiveauEducation() {
    this.patientService.GetNiveauxEducations().subscribe(
      data => {
        this.niveauxEducation = data;
        console.log('Niveaux Education:', this.niveauxEducation); // Check the output here
      },
      error => {
        console.error('Error fetching niveaux education:', error);
      }
    );
  }
  
  loadCategorieMaladie() {
    this.patientService.GetCategorieMaladie().subscribe(
      data => {
        this.categoriesMaladie = data;
        console.log('Categories Maladie:', this.categoriesMaladie); // Check the output here
      },
      error => {
        console.error('Error fetching categories maladie:', error);
      }
    );
  }
  
  private formatDateToYYYYMMDD(date: Date): string {
    return date.toISOString().split('T')[0];
  }

  onSubmit() {
    if (this.patient.dateNaissance) {
      const dateNaissance = new Date(this.patient.dateNaissance);
      this.patient.dateNaissance = this.formatDateToYYYYMMDD(dateNaissance);
    }

    if (this.patient.dateInclusion) {
      const dateInclusion = new Date(this.patient.dateInclusion);
      this.patient.dateInclusion = this.formatDateToYYYYMMDD(dateInclusion);
    }

    this.patientService.create2(this.patient).subscribe(
      (response: HttpResponse<any>) => {
        if (response.status === 200 || response.status === 201) {
          console.log('Patient added successfully', response);
        } else {
          console.error('Unexpected response status', response.status);
        }
      },
      error => {
        console.error('Error adding patient', error);
        if (error.status === 400 && error.error && error.error.errors) {
          console.log('Validation errors:', error.error.errors);
        }
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Patient Form</title>
  <style>
    /* Form Container */
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    /* Form Style */
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    /* Form Input Fields */
    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize; /* Convert label text to title case */
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center; /* Center content horizontally */
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    /* Form Submit Button Hover Effect */
    .form-container form input[type="submit"]:hover {
      background-color: #000;
      color: #fff;
    }

    /* Custom Select Style */
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Three Lines Per Row Layout */
    .form-container .row {
      display: flex;
      justify-content: space-between;
      margin-bottom: 15px;
    }

    .form-container .row .column {
      width: 31%;
    }

    .form-container .row .column.two-cols {
      width: 48%;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" action="/submit-patient">

      <!-- First Row -->
      <div class="row">
        <!-- Left Column -->
        <div class="column">
          <div>
            <label for="numeroDossier">Numero Dossier:</label>
            <input type="text" id="numeroDossier" [(ngModel)]="patient.numeroDossier" name="numeroDossier" required>
          </div>
          <div>
            <label for="nom">Nom:</label>
            <input type="text" id="nom" [(ngModel)]="patient.nom" name="nom" required>
          </div>
          <div>
            <label for="prenom">Prenom:</label>
            <input type="text" id="prenom" [(ngModel)]="patient.prenom" name="prenom" required>
          </div>
        </div>

        <!-- Middle Column -->
        <div class="column">
          <div>
            <label for="dateNaissance">Date Naissance:</label>
            <input type="date" id="dateNaissance" [(ngModel)]="patient.dateNaissance" name="dateNaissance" required>
          </div>
          <div>
            <label for="sexe">Sexe:</label>
            <select id="sexe" [(ngModel)]="patient.sexe" name="sexe" required>
              <option value="M">Male</option>
              <option value="F">Female</option>
            </select>
          </div>
          <div>
            <label for="residence">Residence</label>
            <select id="residence" [(ngModel)]="patient.residence" name="residence" required>
              <option value="Urbaine">Urbaine</option>
              <option value="Rurale">Rurale</option>
            </select>
          </div>
          
        </div>

        <!-- Right Column -->
        <div class="column">
          <div>
            <label for="addresse">Addresse:</label>
            <input type="text" id="addresse" [(ngModel)]="patient.addresse" name="addresse" required>
          </div>
          <div>
            <label for="numTelephone">Num Telephone:</label>
            <input type="text" id="numTelephone" [(ngModel)]="patient.numTelephone" name="numTelephone" required>
          </div>
          <div>
            <label>CNAM:</label>
            <div>
              <input type="radio" id="cnamOui" [(ngModel)]="patient.cnam" [value]="true" name="cnam" required>
              <label for="cnamOui">Oui</label>
              
              <input type="radio" id="cnamNon" [(ngModel)]="patient.cnam" [value]="false" name="cnam" required>
              <label for="cnamNon">Non</label>
            </div>
          </div>
          
        </div>
      </div>

      <!-- Second Row -->
      <div class="row">
        <div class="column">
          <label for="niveauEducationId">Niveau Education:</label>
          <select id="niveauEducationId" [(ngModel)]="patient.niveauEducationId" name="niveauEducationId" required>
            <option *ngFor="let niveau of niveauxEducation" [value]="niveau.niveauEducationId">{{ niveau.niveauEducationDescription }}</option>
          </select>
        </div>
      
        <div class="column">
          <label for="activieProfessionnelle">Activie Professionnelle:</label>
          <input type="checkbox" id="activieProfessionnelle" [(ngModel)]="patient.activieProfessionnelle" name="activieProfessionnelle">
        </div>
     <!-- Consanguinite Parents -->
<div class="column">
  <label for="consanguiniteParents">Consanguinite Parents:</label>
  <input type="checkbox" id="consanguiniteParents" [(ngModel)]="patient.consanguiniteParents" name="consanguiniteParents">
</div>

<!-- Specification Consanguinite Parents Dropdown -->
<div *ngIf="patient.consanguiniteParents">
  <label for="specificationConsanguiniteParents">Specification Consanguinite Parents:</label>
  <select id="specificationConsanguiniteParents" [(ngModel)]="patient.specificationConsanguiniteParents" name="specificationConsanguiniteParents" required>
    <option value="1er degré">1er degré</option>
    <option value="2éme degré">2éme degré</option>
    <option value="Au delà">Au delà</option>
  </select>
</div>

      </div>

      <!-- Third Row -->
      <div class="row">
        
        <!-- Hemoglobinopathie Familiale -->
<div class="column">
  <label for="hemoglobinopathieFamiliale">Hemoglobinopathie Familiale:</label>
  <input type="checkbox" id="hemoglobinopathieFamiliale" [(ngModel)]="patient.hemoglobinopathieFamiliale" name="hemoglobinopathieFamiliale">
</div>

<!-- Additional Checkboxes -->
<div *ngIf="patient.hemoglobinopathieFamiliale" class="additional-checkboxes">
  <label>Père:</label>
  <input type="checkbox" [(ngModel)]="patient.Pere" name="Père">
  
  <label>Mère:</label>
  <input type="checkbox" [(ngModel)]="patient.Mere" name="Mère">
  
  <label>Sœur:</label>
  <input type="checkbox" [(ngModel)]="patient.Soeur" name="Soeur">
  
  <label>Frère:</label>
  <input type="checkbox" [(ngModel)]="patient.Frere" name="Frère">
  
  <label>Autre:</label>
  <input type="checkbox" [(ngModel)]="patient.Autre" name="Autre">
</div>

        <div class="column">
          <label for="nbrSiblings">Nbr Siblings:</label>
          <input type="number" id="nbrSiblings" [(ngModel)]="patient.nbrSiblings" name="nbrSiblings" required>
        </div>
      </div>

      <!-- Fourth Row -->
      <div class="row">
        <div class="column">
          <label for="nbrSiblingsAvecMaladie">Nbr Siblings Avec Maladie:</label>
          <input type="number" id="nbrSiblingsAvecMaladie" [(ngModel)]="patient.nbrSiblingsAvecMaladie" name="nbrSiblingsAvecMaladie" required>
        </div>
        <div class="column">
          <label for="notionConseilGenetiquePourParents">Notion Conseil Genetique Pour Parents:</label>
          <input type="checkbox" id="notionConseilGenetiquePourParents" [(ngModel)]="patient.notionConseilGenetiquePourParents" name="notionConseilGenetiquePourParents">
        </div>
        <div class="column">
          <label for="maladieId">Maladie:</label>
          <select id="maladieId" [(ngModel)]="patient.maladieId" name="maladieId" required>
            <option *ngFor="let maladie of categoriesMaladie" [value]="maladie.maladieId">{{ maladie.descriptionMaladie }}</option>
          </select>
        </div>
        
      </div>

      <!-- Fifth Row -->
      <div class="row">
        <div class="column">
          <label for="dateInclusion">Date Inclusion:</label>
          <input type="date" id="dateInclusion" [(ngModel)]="patient.dateInclusion" name="dateInclusion" required>
        </div>
        <div class="column">
          <label for="inclusionPar">Inclusion Par:</label>
          <input type="number" id="inclusionPar" [(ngModel)]="patient.inclusionPar" name="inclusionPar" required>
        </div>
        <div class="column">
          <label for="recycleBin">Recycle Bin:</label>
          <input type="checkbox" id="recycleBin" [(ngModel)]="patient.recycleBin" name="recycleBin">
        </div>
      </div>

      <!-- Sixth Row -->
      <div class="row">
        <div class="column">
          <label for="archive">Archive:</label>
          <input type="checkbox" id="archive" [(ngModel)]="patient.archive" name="archive">
        </div>
        <div class="column">
          <label for="raisonArchive">Raison Archive:</label>
          <input type="text" id="raisonArchive" [(ngModel)]="patient.raisonArchive" name="raisonArchive">
        </div>
        <div class="column">
          <label for="hospitalisation">Hospitalisation:</label>
          <input type="checkbox" id="hospitalisation" [(ngModel)]="patient.hospitalisation" name="hospitalisation">
        </div>
        <div class="column">
          <label for="nbrHospitalisation">Nbr Hospitalisation:</label>
          <input type="number" id="nbrHospitalisation" [(ngModel)]="patient.nbrHospitalisation" name="nbrHospitalisation" required>
        </div>
      </div>

      <!-- Submit Button Row -->
      <div class="row">
        <div class="column" style="width: 100%; text-align: center;">
          <div class="submit-row">
            <input type="submit" value="Submit">
          </div>
        </div>
      </div>
    </form>
  </div>
</body>
</html>
