import { Component } from '@angular/core';
import { ComplicationCoeur } from 'src/app/models/ComplicationCoeur';
import { CoeurserviceService } from 'src/app/services/coeurservice.service';
import { HttpErrorResponse, HttpResponse } from '@angular/common/http';

@Component({
  selector: 'app-complicationcoeur',
  templateUrl: './complicationcoeur.component.html',
  styleUrls: ['./complicationcoeur.component.css']
})
export class ComplicationcoeurComponent {

  ComplicationCoeur:ComplicationCoeur = new ComplicationCoeur();
  errorMessage: string = '';
  innerExceptionMessage: string = '';

  constructor(private Coeurservice: CoeurserviceService) { }

  onSubmit() {
    console.log('Submitting form with data:', this.ComplicationCoeur);

    this.Coeurservice.addcompcoeur(this.ComplicationCoeur).subscribe(
      response => {
        console.log('Complication added successfully', response);
        this.errorMessage = '';
        this.innerExceptionMessage = '';
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Carte', error);
        this.errorMessage = error.error.message;
        this.innerExceptionMessage = error.error.innerException;
      }
    );
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Patient Form</title>
  <style>
    /* Form Container */
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    /* Form Style */
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    /* Form Input Fields */
    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize; /* Convert label text to title case */
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center; /* Center content horizontally */
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    /* Form Submit Button Hover Effect */
    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    /* Custom Select Style */
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Three Lines Per Row Layout */
    .form-container .row {
      display: flex;
      justify-content: space-between;
      margin-bottom: 15px;
    }

    .form-container .row .column {
      width: 31%;
    }

    .form-container .row .column.two-cols {
      width: 48%;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" >

      
        <div class="column">
          <div>
            <label for="PatientId">PatientId</label>
            <input type="number" id="patientId" [(ngModel)]="ComplicationCoeur.PatientId" name="PatientId" required>
          </div>
          <div>
            <label>insuffaisance cardiaque:</label>
            <div>
              <input type="radio" id="insOui" [(ngModel)]="ComplicationCoeur.ArretCardiaque" [value]="true" name="ArretCardiaque" required>
              <label for="insOui">Oui</label>
              <input type="radio" id="insNon" [(ngModel)]="ComplicationCoeur.ArretCardiaque" [value]="false" name="ArretCardiaque" required>
              <label for="insNon">Non</label>
            </div>
          </div>
          <div>
            <label>Echographie:</label>
            <div>
              <input type="radio" id="einsOui" [(ngModel)]="ComplicationCoeur.Echocardiographie" [value]="true" name="Echocardiographie" required>
              <label for="insOui">Oui</label>
              <input type="radio" id="einsNon" [(ngModel)]="ComplicationCoeur.Echocardiographie" [value]="false" name="Echocardiographie" required>
              <label for="insNon">Non</label>
            </div>
          </div>
          <!-- Additional texts -->
          <div *ngIf="ComplicationCoeur.Echocardiographie" class="additional-texts">
            <label>Hb HBA (%)</label>
            <input type="number" [(ngModel)]="sddiagnostic.HbHbA" name="HbHbA">

            <label>Hb S (%)</label>
            <input type="number" [(ngModel)]="sddiagnostic.HbS" name="HbS">

            <label>Hb A2 (%)</label>
            <input type="number" [(ngModel)]="sddiagnostic.HbA2" name="HbA2">

            <label>Hb F (%)</label>
            <input type="number" [(ngModel)]="sddiagnostic.HbF" name="HbF">

            <label>Autre Hb</label>
            <input type="text" [(ngModel)]="sddiagnostic.AutreHb" name="AutreHb">
          </div>
        </div>
        <div class="submit-row">
          <div *ngIf="errorMessage">
            <p>{{ errorMessage }}</p>
            <p *ngIf="innerExceptionMessage">{{ innerExceptionMessage }}</p>
          </div>
          <button type="submit">Submit</button>          </div>

      

    </form>
  </div>
</body>
</html>
