import { Component, OnInit } from '@angular/core';
import { ComplicationCoeur } from 'src/app/models/ComplicationCoeur';
import { CoeurserviceService } from 'src/app/services/coeurservice.service';

@Component({
  selector: 'app-complication-coeur',
  templateUrl: './complicationcoeur-update.component.html',
  styleUrls: ['./complicationcoeur-update.component.css']
})
export class ComplicationCoeurComponent implements OnInit {
  ComplicationCoeur: ComplicationCoeur = new ComplicationCoeur();
  errorMessage: string | null = null;

  constructor(private coeurService1: CoeurserviceService) {}

  ngOnInit(): void {}

  onPatientIdChange(): void {
    if (this.ComplicationCoeur.PatientId) {
      this.fetchPatientData(this.ComplicationCoeur.PatientId);
    }
  }

  fetchPatientData(patientId: number): void {
    this.coeurService1.getComplicationCoeurByPatientId(patientId).subscribe(
      (data: ComplicationCoeur) => {
        this.ComplicationCoeur = data;
        this.errorMessage = null;
      },
      (error) => {
        this.errorMessage = 'Failed to fetch data';
        console.error(error);
      }
    );
  }

  onSubmit(): void {
    this.coeurService1.saveComplicationCoeur(this.ComplicationCoeur).subscribe(
      () => {
        this.errorMessage = null;
        alert('Data saved successfully');
      },
      (error) => {
        this.errorMessage = 'Failed to save data';
        console.error(error);
      }
    );
  }
}
