import { Component } from '@angular/core';
import { Sddiagnostic } from 'src/app/models/Sddiagnostic';
import { SddiagnosticService } from 'src/app/services/sddiagnostic.service';
import { HttpResponse } from '@angular/common/http';

@Component({
  selector: 'app-sddiagnostic',
  templateUrl: './sddiagnostic.component.html',
  styleUrls: ['./sddiagnostic.component.css']
})
export class SddiagnosticComponent {
  sddiagnostic: Sddiagnostic = new Sddiagnostic();

  constructor(private sddiagnosticService: SddiagnosticService) {}

  onSubmit() {
    this.sddiagnosticService.addSddiagnostic(this.sddiagnostic).subscribe(
      (response: HttpResponse<any>) => {
        if (response.status === 200 || response.status === 201) {
          console.log('SD Diagnostic added successfully', response);
        } else {
          console.error('Unexpected response status', response.status);
        }
      },
      error => {
        console.error('Error adding SD Diagnostic', error);
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>SD Diagnostic Form</title>
  <style>
    .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }
    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }
    .form-container form div {
      margin-bottom: 15px;
    }
    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
    }
    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }
    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }
    .form-container form input[type="submit"] {
      background-color: #000;
      color: #fff;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }
    .form-container form input[type="submit"]:hover {
      background-color: #444;
    }
    .form-container .row {
      display: flex;
      justify-content: space-between;
      margin-bottom: 15px;
    }
    .form-container .row .column {
      width: 31%;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <div class="row">
        <div class="column">
          <label for="patientId">Patient ID:</label>
          <input type="text" id="patientId" [(ngModel)]="sddiagnostic.PatientId" name="patientId" required>
        </div>
        <div class="column">
          <label for="anemie">Anémie:</label>
          <input type="checkbox" id="anemie" [(ngModel)]="sddiagnostic.Anemie" name="anemie">
        </div>
        <div class="column">
          <label for="splenomegalie">Splénomégalie:</label>
          <input type="checkbox" id="splenomegalie" [(ngModel)]="sddiagnostic.Splenomegalie" name="splenomegalie">
        </div>
      </div>

      <div class="row">
        <div class="column">
          <label for="complicationAigue">Complication Aiguë:</label>
          <input type="checkbox" id="complicationAigue" [(ngModel)]="sddiagnostic.ComplicationAigue" name="complicationAigue">
        </div>
       <!-- Additional Checkboxes -->
        <div *ngIf="sddiagnostic.ComplicationAigue" class="additional-checkboxes">
        <label>Spécification:</label>
            <input type="checkbox" [(ngModel)]="sddiagnostic.SpecificationCa" name="Père">
        </div>
        <div class="column">
          <label for="enqueteFamiliale">Enquête Familiale:</label>
          <input type="checkbox" id="enqueteFamiliale" [(ngModel)]="sddiagnostic.EnqueteFamiliale" name="enqueteFamiliale">
        </div>
      </div>

      <div class="row">
        <div class="column">
          <label for="age">Age:</label>
          <input type="number" id="age" [(ngModel)]="sddiagnostic.Age" name="age">
        </div>
        <div class="column">
          <label for="poids">Poids:</label>
          <input type="number" id="poids" [(ngModel)]="sddiagnostic.Poids" name="poids">
        </div>
        <div class="column">
          <label for="taille">Taille:</label>
          <input type="number" id="taille" [(ngModel)]="sddiagnostic.Taille" name="taille">
        </div>
      </div>

      <div class="row">
        <div class="column">
          <label for="hemoglobine">Hémoglobine:</label>
          <input type="text" id="hemoglobine" [(ngModel)]="sddiagnostic.Hemoglobine" name="hemoglobine">
        </div>
        <div class="column">
          <label for="reticulocyte">Réticulocyte:</label>
          <input type="text" id="reticulocyte" [(ngModel)]="sddiagnostic.Reticulocyte" name="reticulocyte">
        </div>
        <div class="column">
          <label for="nbrGlobulesBlancs">Nbr Globules Blancs:</label>
          <input type="text" id="nbrGlobulesBlancs" [(ngModel)]="sddiagnostic.NbrGlobulesBlancs" name="nbrGlobulesBlancs">
        </div>
      </div>

      <div class="row">
        <div class="column">
          <label for="tauxPlaquette">Taux Plaquette:</label>
          <input type="text" id="tauxPlaquette" [(ngModel)]="sddiagnostic.TauxPlaquette" name="tauxPlaquette">
        </div>
        <div class="column">
          <label for="ferritine">Ferritine:</label>
          <input type="text" id="ferritine" [(ngModel)]="sddiagnostic.Ferritine" name="ferritine">
        </div>
       
        <div class="column">
            <label for="autreComplication">Autre Complication:</label>
            <input type="checkbox" id="autreComplication" [(ngModel)]="sddiagnostic.AutreComplication" name="autreComplication">
        </div>

          <!-- Additional Checkboxes -->
          <div *ngIf="sddiagnostic.AutreComplication" class="additional-checkboxes">
            <label>Spécification:</label>
