import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { EvaluationRendement } from 'src/app/models/EvaluationRendement';
import { RendementService } from 'src/app/services/rendement.service';

@Component({
  selector: 'app-rendement',
  templateUrl: './rendement.component.html',
  styleUrls: ['./rendement.component.css']
})
export class RendementComponent {

  EvaluationRendement: EvaluationRendement = new EvaluationRendement();
  errorMessage: string = '';

  constructor(private RendementService: RendementService) {}
 

  onSubmit() {  
    this.RendementService.addev(this.EvaluationRendement).subscribe(
      response => {
        console.log('Complication added successfully', response);
        this.errorMessage = '';
      },
      (error: HttpErrorResponse) => {
        console.error('Error adding Complication', error);
        this.errorMessage = error.error.title;
        if (error.error.errors) {
          for (const [field, errors] of Object.entries(error.error.errors)) {
            console.error(`Validation error for ${field}:`, errors);
          }
        }
      }
    );
  }
  
}

<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
  
  .form-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 20px;
      background-color: #f0f0f0;
    }

    .form-container h1 {
      text-align: center;
      font-size: 36px;
      font-weight: bold;
      color: #2c3e50;
      margin-bottom: 30px;
      font-family: 'Arial', sans-serif;
      text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
      border-bottom: 3px solid #3498db;
      padding-bottom: 10px;
      display: inline-block;
    }

    .form-container form {
      background-color: #f9f9f9;
      padding: 20px;
      border-radius: 10px;
      box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    }

    .form-container form div {
      margin-bottom: 15px;
    }

    .form-container form label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      text-transform: capitalize;
    }

    .form-container form input,
    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
    }

    .form-container form .submit-row {
      text-align: center;
      margin-top: 20px;
    }

    .form-container form input[type="submit"] {
      background-color: #000 !important;
      color: #fff !important;
      padding: 12px 20px;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      transition: background-color 0.3s ease;
    }

    .form-container form input[type="submit"]:hover {
      background-color: #444;
      color: #fff;
    }

    .form-container form select {
      width: 100%;
      padding: 10px;
      margin: 8px 0;
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #fff;
      font-size: 16px;
      cursor: pointer;
      appearance: none;
      -webkit-appearance: none;
      -moz-appearance: none;
      background-image: url('data:image/svg+xml;utf8,<svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill-rule="evenodd" d="M7.68 11.895a.75.75 0 0 1-1.06-1.06L7.94 9.44V5.75a.75.75 0 0 1 1.5 0v3.69l1.32 1.33a.75.75 0 0 1-1.06 1.06L8 10.56l-.32.32z"/></svg>');
      background-repeat: no-repeat;
      background-position: right 10px top 50%, 0 0;
      background-size: 16px auto, 100%;
    }

    /* Style for CNAM radio buttons */
    .radio-group {
      display: flex;
      align-items: center;
      gap: 15px;
    }

    .radio-group input[type="radio"] {
      margin-right: 5px;
    }

    .radio-group label {
      margin: 0;
    }
  </style>
</head>
<body>
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" >
     
      <h1>Ostéonécrose aseptique de la tête fémorale    </h1>
    
      <!-- Patient Id-->
      <div>
        <label for="PatientId">PatientId</label>
        <input type="number" id="patientId" [(ngModel)]="EvaluationRendement.PatientId" name="PatientId" >
      </div>
     

     <!-- Evaluation -->
     <div>
        <label>Ostéonécrose asptique de la tête fémorale:</label>
        <div class="radio-group">
          <!-- Oui radio button first -->
          <input type="radio" id="evOui" [(ngModel)]="EvaluationRendement.Evaluation" [value]="true" name="Evaluation" required>
          <label for="evOui">Oui</label>
          
          <!-- No radio button second -->
          <input type="radio" id="evNon" [(ngModel)]="EvaluationRendement.Evaluation" [value]="false" name="Evaluation" required>
          <label for="evNon">Non</label>
        </div>
      </div>


      
        <!-- Date apparition -->
        <div *ngIf="EvaluationRendement.Evaluation">
            <label for="rendement">Date d'apparition</label>
            <input type="date" id="DateApparition" [(ngModel)]="EvaluationRendement.DateApparition" name="DateApparition" >
          </div>
      

          <!-- Siege -->
          <div *ngIf="EvaluationRendement.Evaluation">
            <label for="Siege">Siege</label>
            <input type="text" id="Siege" [(ngModel)]="EvaluationRendement.Siege" name="Siege" >
          </div>
  
           <!-- Stade -->
           <div *ngIf="EvaluationRendement.Evaluation">
            <label for="Stade">Stade</label>
            <input type="text" id="Stade" [(ngModel)]="EvaluationRendement.Stade" name="Stade" >
          </div>
          <!-- Symptomatique -->
          <div *ngIf="EvaluationRendement.Evaluation">
            <label for="Symptomatique">Symptomatique</label>
            <input type="checkbox" id="Symptomatique" [(ngModel)]="EvaluationRendement.Symptomatique" name="Symptomatique" >
          </div>
            <!-- Radio standard -->
            <div *ngIf="EvaluationRendement.Evaluation">

            <label for="RadioStandard">Radio Standard</label>
            <input type="checkbox" id="RadioStandard" [(ngModel)]="EvaluationRendement.RadioStandard" name="RadioStandard" >
          </div>
            <!-- Préciser -->

          <div *ngIf="EvaluationRendement.RadioStandard && EvaluationRendement.Evaluation">
            <label for="TxtRadio">Préciser</label>
            <input type="text" id="TxtRadio" [(ngModel)]="EvaluationRendement.TxtRadio" name="TxtRadio" >
          </div>
         
        <!-- rendement -->
