import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { ComplicationCrisesDouleureuse } from 'src/app/models/ComplicationCrisesDouleureuse';
import { CriseService } from 'src/app/services/crise.service';

@Component({
  selector: 'app-crise',
  templateUrl: './crise.component.html',
  styleUrls: ['./crise.component.css']
})
export class CriseComponent {
  crises: ComplicationCrisesDouleureuse[] = [new ComplicationCrisesDouleureuse()];
  errorMessage: string = '';

  constructor(private CriseService: CriseService) {}

  private formatDateToYYYYMMDD(date: Date): string {
    return date.toISOString().split('T')[0];
  }

  addCriseForm() {
    this.crises.push(new ComplicationCrisesDouleureuse());
  }

  onSubmit() {
    const now = new Date();

    this.crises.forEach(crise => {
      crise.Date = crise.Date ? this.formatDateToYYYYMMDD(new Date(crise.Date)) : this.formatDateToYYYYMMDD(now);

      // Convertir les dates en format ISO 8601 avant de les envoyer au backend
      const complicationToSend = {
        ...crise,
        DateEchocardiographie: new Date(crise.Date).toISOString(),
      };

      this.CriseService.addcrise(complicationToSend).subscribe(
        response => {
          console.log('Complication added successfully', response);
          this.errorMessage = '';
        },
        (error: HttpErrorResponse) => {
          console.error('Error adding complication', error);
          this.errorMessage = error.error.title;
          if (error.error.errors) {
            for (const [field, errors] of Object.entries(error.error.errors)) {
              console.error(`Validation error for ${field}:`, errors);
            }
          }
        }
      );
    });
  }
}
